import js from '@eslint/js';
import reactHooks from 'eslint-plugin-react-hooks';
import reactRefresh from 'eslint-plugin-react-refresh';
import globals from 'globals';

// Minimal helpers to allow the config to be evaluated in this environment
function defineConfig(x) { return x; }
function globalIgnores(arr) { return { ignores: arr }; }

export default defineConfig([
  globalIgnores(['dist', 'node_modules', 'build', '.eslintrc.js']),
  {
    files: ['**/*.{js,jsx}'],
    languageOptions: {
      ecmaVersion: 2020,
      globals: {
        ...globals.browser,
        ...globals.es2020,
        ...globals.node,
      },
      parserOptions: {
        ecmaVersion: 'latest',
        ecmaFeatures: { jsx: true },
        sourceType: 'module',
      },
    },
    rules: {
      // Keep core rules
      'no-unused-vars': 'error',
      'no-irregular-whitespace': 'error',
      'no-console': 'error',
      'no-debugger': 'error',
      'prefer-const': 'error',
      // react-hooks rule removed to avoid requiring plugin in this minimal run
    },
  },
])

import React, { Suspense, lazy } from "react";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";

// Import Layout Components
import AppLayout from "./components/layout/AppLayout.jsx";
import { LoadingScreen } from "./components/ui/LoadingSpinner.jsx";

// Import Context Providers
import { AuthProvider } from "./contexts/AuthContext.jsx";
import { CourseProvider } from "./contexts/CourseContext.jsx";
import { UserProvider } from "./contexts/UserContext.jsx";
import { PaymentProvider } from "./contexts/PaymentContext.jsx";
import { LearnPageProvider } from "./contexts/LearnPageContext.jsx";
import { RealtimeProvider } from "./contexts/RealtimeContext.jsx";
import NotFound from "./components/Error/NotFound.jsx";

// Import Page Components
import ProtectedRoute from "./components/Auth/ProtectedRoute.jsx";
import SignIn from "./pages/SignIn.jsx";
import SignUp from "./pages/SignUp.jsx";
import LandingPage from "./pages/LandingPage.jsx";
import CoursePage from "./pages/CoursePage.jsx";
import CourseDetailsPage from "./pages/CourseDetailsPage.jsx";
import LearnPage from "./pages/LearnPage.jsx";
import CheckoutPage from "./pages/CheckoutPage.jsx";
import ProfilePage from "./pages/ProfilePage.jsx";
import ProfileEdit from "./pages/ProfileEdit.jsx";
import LegalPage from "./pages/LegalPage.jsx";
import CreateEditCouponPage from "./pages/Admin/CreateEditCouponPage.jsx";

// Lazy load admin components for code splitting
const AdminPage = lazy(() => import("./pages/Admin/AdminPage.jsx"));
const Analytics = lazy(() => import("./pages/Admin/Analytics.jsx"));
const CourseForm = lazy(() => import("./pages/Admin/CourseForm.jsx"));
const UsersManagement = lazy(() => import("./pages/Admin/UsersManagement.jsx"));
const AdminCoupons = lazy(() => import("./pages/Admin/AdminCoupons.jsx"));
const Courses = lazy(() => import("./pages/Admin/Courses.jsx"));

const App = () => {
  return (
    <Router>
      {/* Start of Nested Providers */}
      <AuthProvider>
        <UserProvider>
          <CourseProvider>
            <PaymentProvider>
              <LearnPageProvider>
                <RealtimeProvider>
                  {/* The AppLayout wraps the visible parts of the application */}
                  <AppLayout>
                    <Routes>
                      {/* Public Routes */}
                      <Route path="/" element={<LandingPage />} />
                      <Route path="/courses" element={<CoursePage />} />
                      <Route
                        path="/course/:courseId"
                        element={<CourseDetailsPage />}
                      />
                      <Route path="/legal/:page" element={<LegalPage />} />

                      {/* Auth Routes (No Header/Footer) */}
                      <Route path="/auth/signin" element={<SignIn />} />
                      <Route path="/auth/signup" element={<SignUp />} />

                      {/* Protected User Routes */}
                      <Route
                        path="/profile"
                        element={
                          <ProtectedRoute>
                            <ProfilePage />
                          </ProtectedRoute>
                        }
                      />
                       <Route
                        path="/profile/edit"
                        element={
                          <ProtectedRoute>
                            <ProfileEdit />
                          </ProtectedRoute>
                        }
                      />
                      <Route
                        path="/learn/:courseId"
                        element={
                          <ProtectedRoute>
                            <LearnPage />
                          </ProtectedRoute>
                        }
                      />
                      <Route
                        path="/checkout/:courseId"
                        element={
                          <ProtectedRoute>
                            <CheckoutPage />
                          </ProtectedRoute>
                        }
                      />

                      {/* Admin Routes with Code Splitting */}
                      <Route
                        path="/admin"
                        element={
                          <ProtectedRoute requiredRole="admin">
                            <Suspense fallback={<LoadingScreen />}>
                              <AdminPage />
                            </Suspense>
                          </ProtectedRoute>
                        }
                      />
                      <Route
                        path="/admin/analytics"
                        element={
                          <ProtectedRoute requiredRole="admin">
                            <Suspense fallback={<LoadingScreen />}>
                              <Analytics />
                            </Suspense>
                          </ProtectedRoute>
                        }
                      />
                      <Route
                        path="/admin/users"
                        element={
                          <ProtectedRoute requiredRole="admin">
                            <Suspense fallback={<LoadingScreen />}>
                              <UsersManagement />
                            </Suspense>
                          </ProtectedRoute>
                        }
                      />
                      <Route
                        path="/admin/courses"
                        element={
                          <ProtectedRoute requiredRole="admin">
                            <Suspense fallback={<LoadingScreen />}>
                              <Courses />
                            </Suspense>
                          </ProtectedRoute>
                        }
                      />
                      <Route
                        path="/admin/coupons"
                        element={
                          <ProtectedRoute requiredRole="admin">
                            <Suspense fallback={<LoadingScreen />}>
                              <AdminCoupons />
                            </Suspense>
                          </ProtectedRoute>
                        }
                      />
                      <Route
                        path="/admin/courses/edit/:courseId"
                        element={
                          <ProtectedRoute requiredRole="admin">
                            <Suspense fallback={<LoadingScreen />}>
                              <CourseForm />
                            </Suspense>
                          </ProtectedRoute>
                        }
                      />

                      <Route
                        path="/admin/coupons/create"
                        element={
                          <ProtectedRoute requiredRole="admin">
                            <Suspense fallback={<LoadingScreen />}>
                              {/* No couponId means creation mode */}
                              <CreateEditCouponPage />
                            </Suspense>
                          </ProtectedRoute>
                        }
                      />

                      {/* CORRECTED: Admin Coupon Edit Route */}
                      <Route
                        path="/admin/coupons/edit/:couponId"
                        element={
                          <ProtectedRoute requiredRole="admin">
                            <Suspense fallback={<LoadingScreen />}>
                              <CreateEditCouponPage />
                            </Suspense>
                          </ProtectedRoute>
                        }
                      />
                      <Route
                        path="/admin/courses/create/new"
                        element={
                          <ProtectedRoute requiredRole="admin">
                            <Suspense fallback={<LoadingScreen />}>
                              <CourseForm />
                            </Suspense>
                          </ProtectedRoute>
                        }
                      />

                      {/* 404 Route */}
                      <Route path="*" element={<NotFound />} />
                    </Routes>
                  </AppLayout>
                </RealtimeProvider>
              </LearnPageProvider>
            </PaymentProvider>
          </CourseProvider>
        </UserProvider>
      </AuthProvider>
      {/* End of Nested Providers */}
    </Router>
  );
};

export default App;

import React from "react";

const BasicInfoTab = ({ course, handleCourseChange, errors }) => {
  return (
    <div className="bg-white rounded-xl shadow-md border border-gray-200 p-6">
      <h2 className="text-xl font-bold text-gray-900 mb-6">
        Basic Information
      </h2>

      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        {/* Course Title */}
        <div>
          <label className="block text-sm font-medium text-gray-700 mb-2">
            Course Title *
          </label>
          <input
            type="text"
            value={course.title}
            onChange={(e) => handleCourseChange("title", e.target.value)}
            className={`w-full p-3 border rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500 ${
              errors.title ? "border-red-500" : "border-gray-300"
            }`}
            placeholder="Enter course title"
            required
          />
          {errors.title && (
            <p className="text-red-500 text-sm mt-1">{errors.title}</p>
          )}
        </div>

        {/* Category */}
        <div>
          <label className="block text-sm font-medium text-gray-700 mb-2">
            Category *
          </label>
          <select
            value={course.category}
            onChange={(e) => handleCourseChange("category", e.target.value)}
            className="w-full p-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
          >
            <option value="web-development">Web Development</option>
            <option value="mobile-development">Mobile Development</option>
            <option value="data-science">Data Science</option>
            <option value="design">Design</option>
            <option value="business">Business</option>
          </select>
        </div>

        {/* Short Description */}
        <div className="lg:col-span-2">
          <label className="block text-sm font-medium text-gray-700 mb-2">
            Short Description *
          </label>
          <input
            type="text"
            value={course.shortDescription}
            onChange={(e) =>
              handleCourseChange("shortDescription", e.target.value)
            }
            className={`w-full p-3 border rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500 ${
              errors.shortDescription ? "border-red-500" : "border-gray-300"
            }`}
            placeholder="Brief description that appears in course cards"
            required
          />
          {errors.shortDescription && (
            <p className="text-red-500 text-sm mt-1">
              {errors.shortDescription}
            </p>
          )}
        </div>

        {/* Full Description */}
        <div className="lg:col-span-2">
          <label className="block text-sm font-medium text-gray-700 mb-2">
            Full Description *
          </label>
          <textarea
            value={course.description}
            onChange={(e) => handleCourseChange("description", e.target.value)}
            rows={6}
            className={`w-full p-3 border rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500 ${
              errors.description ? "border-red-500" : "border-gray-300"
            }`}
            placeholder="Detailed course description that appears on the course page"
            required
          />
          {errors.description && (
            <p className="text-red-500 text-sm mt-1">{errors.description}</p>
          )}
        </div>

        {/* Instructor */}
        <div>
          <label className="block text-sm font-medium text-gray-700 mb-2">
            Instructor *
          </label>
          <input
            type="text"
            value={course.instructor}
            onChange={(e) => handleCourseChange("instructor", e.target.value)}
            className={`w-full p-3 border rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500 ${
              errors.instructor ? "border-red-500" : "border-gray-300"
            }`}
            placeholder="Instructor name"
            required
          />
          {errors.instructor && (
            <p className="text-red-500 text-sm mt-1">{errors.instructor}</p>
          )}
        </div>

        {/* Duration */}
        <div>
          <label className="block text-sm font-medium text-gray-700 mb-2">
            Duration *
          </label>
          <input
            type="text"
            value={course.duration}
            onChange={(e) => handleCourseChange("duration", e.target.value)}
            className={`w-full p-3 border rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500 ${
              errors.duration ? "border-red-500" : "border-gray-300"
            }`}
            placeholder="e.g., 12 hours"
            required
          />
          {errors.duration && (
            <p className="text-red-500 text-sm mt-1">{errors.duration}</p>
          )}
        </div>

        {/* Level */}
        <div>
          <label className="block text-sm font-medium text-gray-700 mb-2">
            Level *
          </label>
          <select
            value={course.level}
            onChange={(e) => handleCourseChange("level", e.target.value)}
            className="w-full p-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
          >
            <option value="beginner">Beginner</option>
            <option value="intermediate">Intermediate</option>
            <option value="advanced">Advanced</option>
          </select>
        </div>

        {/* Language */}
        <div>
          <label className="block text-sm font-medium text-gray-700 mb-2">
            Language *
          </label>
          <select
            value={course.language}
            onChange={(e) => handleCourseChange("language", e.target.value)}
            className="w-full p-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
          >
            <option value="english">English</option>
            <option value="hindi">Hindi</option>
            <option value="spanish">Spanish</option>
          </select>
        </div>
      </div>

      {/* Requirements */}
      <div className="mt-6">
        <label className="block text-sm font-medium text-gray-700 mb-2">
          Requirements
        </label>
        <textarea
          value={course.requirements?.join("\n") || ""}
          onChange={(e) =>
            handleCourseChange(
              "requirements",
              e.target.value.split("\n").filter((item) => item.trim())
            )
          }
          rows={3}
          className="w-full p-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
          placeholder="Enter each requirement on a new line"
        />
      </div>

      {/* What You'll Learn */}
      <div className="mt-6">
        <label className="block text-sm font-medium text-gray-700 mb-2">
          What Students Will Learn
        </label>
        <textarea
          value={course.whatYouLearn?.join("\n") || ""}
          onChange={(e) =>
            handleCourseChange(
              "whatYouLearn",
              e.target.value.split("\n").filter((item) => item.trim())
            )
          }
          rows={3}
          className="w-full p-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
          placeholder="Enter each learning point on a new line"
        />
      </div>
    </div>
  );
};

export default BasicInfoTab;

import React from 'react';
import { Image as ImageIcon } from 'lucide-react';

const MediaTab = ({ course, handleCourseChange }) => {
    return (
        <div className="bg-white rounded-xl shadow-md border border-gray-200 p-6">
            <h2 className="text-xl font-bold text-gray-900 mb-6">Media & Assets</h2>
            
            <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
                {/* Course Image URL */}
                <div>
                    <label className="block text-sm font-medium text-gray-700 mb-2">Course Image URL</label>
                    <input
                        type="url"
                        value={course.imageUrl}
                        onChange={(e) => handleCourseChange('imageUrl', e.target.value)}
                        className="w-full p-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                        placeholder="https://example.com/image.jpg"
                    />
                    {course.imageUrl && (
                        <div className="mt-2">
                            <img 
                                src={course.imageUrl} 
                                alt="Course preview" 
                                className="w-32 h-20 object-cover rounded-lg border"
                                onError={(e) => {
                                    e.target.src = 'https://via.placeholder.com/150x100?text=Image+Error';
                                }}
                            />
                        </div>
                    )}
                </div>

                {/* Promo Video URL */}
                <div>
                    <label className="block text-sm font-medium text-gray-700 mb-2">Promo Video URL</label>
                    <input
                        type="url"
                        value={course.videoUrl}
                        onChange={(e) => handleCourseChange('videoUrl', e.target.value)}
                        className="w-full p-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                        placeholder="https://youtube.com/embed/..."
                    />
                </div>
            </div>

            {/* Tags */}
            <div className="mt-6">
                <label className="block text-sm font-medium text-gray-700 mb-2">Tags</label>
                <input
                    type="text"
                    value={course.tags?.join(', ') || ''}
                    onChange={(e) => handleCourseChange('tags', e.target.value.split(',').map(tag => tag.trim()).filter(tag => tag))}
                    className="w-full p-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                    placeholder="react, javascript, frontend"
                />
                <p className="text-sm text-gray-500 mt-1">Separate tags with commas</p>
            </div>
        </div>
    );
};

export default MediaTab;

import React from 'react';

const PricingTab = ({ course, handleCourseChange, errors }) => {
    const discountPercent = course.originalPrice > course.price 
        ? Math.round(((course.originalPrice - course.price) / course.originalPrice) * 100) 
        : 0;

    return (
        <div className="bg-white rounded-xl shadow-md border border-gray-200 p-6">
            <h2 className="text-xl font-bold text-gray-900 mb-6">Pricing & Discounts</h2>
            
            <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
                {/* Current Price */}
                <div>
                    <label className="block text-sm font-medium text-gray-700 mb-2">Current Price (₹) *</label>
                    <input
                        type="number"
                        value={course.price}
                        onChange={(e) => handleCourseChange('price', parseInt(e.target.value) || 0)}
                        className={`w-full p-3 border rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500 ${errors.price ? 'border-red-500' : 'border-gray-300'}`}
                        min="0"
                        required
                    />
                    {errors.price && <p className="text-red-500 text-sm mt-1">{errors.price}</p>}
                </div>

                {/* Original Price */}
                <div>
                    <label className="block text-sm font-medium text-gray-700 mb-2">Original Price (₹)</label>
                    <input
                        type="number"
                        value={course.originalPrice}
                        onChange={(e) => handleCourseChange('originalPrice', parseInt(e.target.value) || 0)}
                        className="w-full p-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                        min="0"
                    />
                </div>

                {/* Discount Calculation */}
                {course.originalPrice > course.price && (
                    <div className="lg:col-span-2">
                        <div className="flex items-center space-x-6 p-4 bg-blue-50 rounded-lg">
                            <div className="text-sm">
                                <span className="font-medium">Discount: </span>
                                <span className="text-green-600 font-bold">{discountPercent}%</span>
                            </div>
                            <div className="text-sm">
                                <span className="font-medium">You Save: </span>
                                <span className="text-green-600 font-bold">₹{course.originalPrice - course.price}</span>
                            </div>
                        </div>
                    </div>
                )}
            </div>

            {/* Course Status */}
            <div className="mt-6 grid grid-cols-1 md:grid-cols-3 gap-4">
                <div className="flex items-center">
                    <input
                        type="checkbox"
                        id="isPublished"
                        checked={course.isPublished}
                        onChange={(e) => handleCourseChange('isPublished', e.target.checked)}
                        className="h-4 w-4 text-blue-600 focus:ring-blue-500 border-gray-300 rounded"
                    />
                    <label htmlFor="isPublished" className="ml-2 block text-sm text-gray-900">Published</label>
                </div>

                <div className="flex items-center">
                    <input
                        type="checkbox"
                        id="isFeatured"
                        checked={course.isFeatured}
                        onChange={(e) => handleCourseChange('isFeatured', e.target.checked)}
                        className="h-4 w-4 text-blue-600 focus:ring-blue-500 border-gray-300 rounded"
                    />
                    <label htmlFor="isFeatured" className="ml-2 block text-sm text-gray-900">Featured</label>
                </div>

                <div className="flex items-center">
                    <input
                        type="checkbox"
                        id="isBestseller"
                        checked={course.isBestseller}
                        onChange={(e) => handleCourseChange('isBestseller', e.target.checked)}
                        className="h-4 w-4 text-blue-600 focus:ring-blue-500 border-gray-300 rounded"
                    />
                    <label htmlFor="isBestseller" className="ml-2 block text-sm text-gray-900">Bestseller</label>
                </div>
            </div>
        </div>
    );
};

export default PricingTab;

import React, { useState } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '../ui/Card';
import Button from '../ui/Button';
import Badge from '../ui/Badge';
import FormField from '../ui/FormField';
import { Alert, AlertDescription, AlertIcon } from '../ui/Alert';
import CourseList from '../course/CourseList';
import { useRealtime } from '../../contexts/RealtimeContext';
import { useFormValidation, validationRules } from '../../hooks/useFormValidation';
import { 
  Plus, 
  Edit, 
  Eye, 
  Trash2, 
  Users, 
  TrendingUp,
  Clock,
  CheckCircle,
  AlertCircle
} from 'lucide-react';
import { cn } from '../../utils/cn';

const RealtimeCourseManagement = () => {
  const {
    courses,
    adminEnrollments,
    coursesLoading,
    adminEnrollmentsLoading,
    coursesError,
    updateCourse,
    createCourse,
    courseMutationsLoading,
    courseMutationsError
  } = useRealtime();

  const [showCreateForm, setShowCreateForm] = useState(false);
  const [editingCourse, setEditingCourse] = useState(null);
  const [isSubmitting, setIsSubmitting] = useState(false);

  const { values, errors, handleChange, handleBlur, validateForm, resetForm } = useFormValidation(
    {
      title: '',
      instructor: '',
      description: '',
      price: '',
      duration: '',
      category: '',
      difficulty: ''
    },
    {
      title: [validationRules.required('Course title is required')],
      instructor: [validationRules.required('Instructor name is required')],
      description: [
        validationRules.required('Description is required'),
        validationRules.minLength(20, 'Description must be at least 20 characters')
      ],
      price: [
        validationRules.required('Price is required'),
        validationRules.positiveNumber('Price must be a positive number')
      ],
      duration: [
        validationRules.required('Duration is required'),
        validationRules.positiveNumber('Duration must be a positive number')
      ],
      category: [validationRules.required('Please select a category')],
      difficulty: [validationRules.required('Please select difficulty level')]
    }
  );

  const formatCurrency = (amount) => {
    return new Intl.NumberFormat('en-IN', {
      style: 'currency',
      currency: 'INR',
      minimumFractionDigits: 0
    }).format(amount / 100);
  };

  const getCourseStats = (courseId) => {
    const enrollments = adminEnrollments?.filter(e => e.courseId === courseId) || [];
    const totalEnrollments = enrollments.length;
    const totalRevenue = enrollments.reduce((sum, e) => sum + (e.amount || 0), 0);
    const completionRate = enrollments.length > 0 
      ? enrollments.filter(e => e.progress?.completionPercentage >= 100).length / enrollments.length * 100
      : 0;

    return {
      totalEnrollments,
      totalRevenue,
      completionRate: Math.round(completionRate)
    };
  };

  const handleTogglePublish = async (courseId, currentStatus) => {
    const result = await updateCourse(courseId, {
      isPublished: !currentStatus,
      status: !currentStatus ? 'published' : 'draft'
    });

    if (result.success) {
      // Success feedback could be shown here
      console.log('Course status updated successfully');
    } else {
      console.error('Failed to update course status:', result.error);
    }
  };

  const handleToggleFeatured = async (courseId, currentStatus) => {
    const result = await updateCourse(courseId, {
      isFeatured: !currentStatus
    });

    if (result.success) {
      console.log('Course featured status updated successfully');
    } else {
      console.error('Failed to update course featured status:', result.error);
    }
  };

  if (coursesError) {
    return (
      <Alert variant="destructive">
        <AlertIcon variant="destructive" />
        <AlertDescription>
          Error loading courses: {coursesError}
        </AlertDescription>
      </Alert>
    );
  }

  if (courseMutationsError) {
    return (
      <Alert variant="destructive">
        <AlertIcon variant="destructive" />
        <AlertDescription>
          Error with course operations: {courseMutationsError}
        </AlertDescription>
      </Alert>
    );
  }

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div>
          <h2 className="text-2xl font-bold">Course Management</h2>
          <p className="text-muted-foreground">
            Manage courses in real-time. Changes are reflected immediately.
          </p>
        </div>
        <Button onClick={() => setShowCreateForm(true)}>
          <Plus className="h-4 w-4 mr-2" />
          Create Course
        </Button>
      </div>

      {/* Real-time Status */}
      <div className="flex items-center gap-4">
        <Badge variant={coursesLoading ? 'secondary' : 'success'}>
          {coursesLoading ? (
            <>
              <Clock className="h-3 w-3 mr-1" />
              Syncing...
            </>
          ) : (
            <>
              <CheckCircle className="h-3 w-3 mr-1" />
              Live
            </>
          )}
        </Badge>
        <span className="text-sm text-muted-foreground">
          {courses?.length || 0} courses • Last updated: {new Date().toLocaleTimeString()}
        </span>
      </div>

      {/* Course Stats Overview */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
        <Card>
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">Total Courses</CardTitle>
            <TrendingUp className="h-4 w-4 text-blue-600" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold">{courses?.length || 0}</div>
            <p className="text-xs text-muted-foreground">
              {courses?.filter(c => c.isPublished).length || 0} published
            </p>
          </CardContent>
        </Card>

        <Card>
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">Total Enrollments</CardTitle>
            <Users className="h-4 w-4 text-green-600" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold">
              {adminEnrollments?.length || 0}
            </div>
            <p className="text-xs text-muted-foreground">
              Across all courses
            </p>
          </CardContent>
        </Card>

        <Card>
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">Total Revenue</CardTitle>
            <TrendingUp className="h-4 w-4 text-orange-600" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold">
              {formatCurrency(
                adminEnrollments?.reduce((sum, e) => sum + (e.amount || 0), 0) || 0
              )}
            </div>
            <p className="text-xs text-muted-foreground">
              From enrollments
            </p>
          </CardContent>
        </Card>
      </div>

      {/* Courses List */}
      {coursesLoading ? (
        <div className="text-center py-8">
          <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary mx-auto"></div>
          <p className="text-muted-foreground mt-2">Loading courses...</p>
        </div>
      ) : courses && courses.length > 0 ? (
        <div className="space-y-4">
          {courses.map((course) => {
            const stats = getCourseStats(course.id);
            return (
              <Card key={course.id} className="hover:shadow-md transition-shadow">
                <CardHeader>
                  <div className="flex items-start justify-between">
                    <div className="flex-1">
                      <CardTitle className="text-lg">{course.title}</CardTitle>
                      <p className="text-sm text-muted-foreground mt-1">
                        {course.description || 'No description available'}
                      </p>
                    </div>
                    <div className="flex items-center gap-2 ml-4">
                      <Badge 
                        variant={course.isPublished ? 'success' : 'secondary'}
                      >
                        {course.isPublished ? 'Published' : 'Draft'}
                      </Badge>
                      {course.isFeatured && (
                        <Badge variant="warning">Featured</Badge>
                      )}
                    </div>
                  </div>
                </CardHeader>
                <CardContent>
                  <div className="grid grid-cols-1 md:grid-cols-4 gap-4 mb-4">
                    <div className="text-center">
                      <p className="text-2xl font-bold text-blue-600">
                        {formatCurrency(course.price || 0)}
                      </p>
                      <p className="text-xs text-muted-foreground">Price</p>
                    </div>
                    <div className="text-center">
                      <p className="text-2xl font-bold text-green-600">
                        {stats.totalEnrollments}
                      </p>
                      <p className="text-xs text-muted-foreground">Enrollments</p>
                    </div>
                    <div className="text-center">
                      <p className="text-2xl font-bold text-orange-600">
                        {formatCurrency(stats.totalRevenue)}
                      </p>
                      <p className="text-xs text-muted-foreground">Revenue</p>
                    </div>
                    <div className="text-center">
                      <p className="text-2xl font-bold text-purple-600">
                        {stats.completionRate}%
                      </p>
                      <p className="text-xs text-muted-foreground">Completion</p>
                    </div>
                  </div>

                  <div className="flex items-center gap-2">
                    <Button
                      variant="outline"
                      size="sm"
                      onClick={() => handleTogglePublish(course.id, course.isPublished)}
                      disabled={courseMutationsLoading}
                    >
                      {course.isPublished ? 'Unpublish' : 'Publish'}
                    </Button>
                    <Button
                      variant="outline"
                      size="sm"
                      onClick={() => handleToggleFeatured(course.id, course.isFeatured)}
                      disabled={courseMutationsLoading}
                    >
                      {course.isFeatured ? 'Remove Featured' : 'Make Featured'}
                    </Button>
                    <Button
                      variant="outline"
                      size="sm"
                      onClick={() => setEditingCourse(course)}
                    >
                      <Edit className="h-4 w-4 mr-1" />
                      Edit
                    </Button>
                    <Button
                      variant="outline"
                      size="sm"
                      asChild
                    >
                      <a href={`/course/${course.id}`} target="_blank" rel="noopener noreferrer">
                        <Eye className="h-4 w-4 mr-1" />
                        View
                      </a>
                    </Button>
                  </div>
                </CardContent>
              </Card>
            );
          })}
        </div>
      ) : (
        <Card>
          <CardContent className="text-center py-8">
            <p className="text-muted-foreground">No courses found</p>
            <Button 
              className="mt-4" 
              onClick={() => setShowCreateForm(true)}
            >
              <Plus className="h-4 w-4 mr-2" />
              Create Your First Course
            </Button>
          </CardContent>
        </Card>
      )}

      {/* Create Course Form */}
      {showCreateForm && (
        <Card>
          <CardHeader>
            <CardTitle>Create New Course</CardTitle>
          </CardHeader>
          <CardContent>
            <form onSubmit={async (e) => {
              e.preventDefault();
              
              if (!validateForm()) {
                return;
              }

              setIsSubmitting(true);

              const courseData = {
                title: values.title,
                instructor: values.instructor,
                description: values.description,
                price: parseInt(values.price) * 100, // Convert to paise
                category: values.category,
                duration: parseInt(values.duration),
                difficulty: values.difficulty
              };

              try {
                const result = await createCourse(courseData);
                if (result.success) {
                  setShowCreateForm(false);
                  resetForm();
                }
              } catch (error) {
                console.error('Error creating course:', error);
              } finally {
                setIsSubmitting(false);
              }
            }}>
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <FormField
                  label="Course Title"
                  type="text"
                  placeholder="Enter course title"
                  required
                  value={values.title}
                  onChange={(value) => handleChange('title', value)}
                  onBlur={() => handleBlur('title')}
                  error={errors.title}
                />
                <FormField
                  label="Instructor"
                  type="text"
                  placeholder="Enter instructor name"
                  required
                  value={values.instructor}
                  onChange={(value) => handleChange('instructor', value)}
                  onBlur={() => handleBlur('instructor')}
                  error={errors.instructor}
                />
                <div className="md:col-span-2">
                  <FormField
                    label="Description"
                    type="textarea"
                    placeholder="Enter course description"
                    required
                    rows={3}
                    value={values.description}
                    onChange={(value) => handleChange('description', value)}
                    onBlur={() => handleBlur('description')}
                    error={errors.description}
                  />
                </div>
                <FormField
                  label="Price (₹)"
                  type="number"
                  placeholder="Enter price in rupees"
                  required
                  min="0"
                  value={values.price}
                  onChange={(value) => handleChange('price', value)}
                  onBlur={() => handleBlur('price')}
                  error={errors.price}
                />
                <FormField
                  label="Duration (hours)"
                  type="number"
                  placeholder="Enter duration in hours"
                  required
                  min="1"
                  value={values.duration}
                  onChange={(value) => handleChange('duration', value)}
                  onBlur={() => handleBlur('duration')}
                  error={errors.duration}
                />
                <FormField
                  label="Category"
                  type="select"
                  required
                  value={values.category}
                  onChange={(value) => handleChange('category', value)}
                  onBlur={() => handleBlur('category')}
                  error={errors.category}
                >
                  <option value="">Select category</option>
                  <option value="ai-ml">AI & Machine Learning</option>
                  <option value="web-development">Web Development</option>
                  <option value="cybersecurity">Cybersecurity</option>
                  <option value="data-science">Data Science</option>
                  <option value="mobile-development">Mobile Development</option>
                </FormField>
                <FormField
                  label="Difficulty"
                  type="select"
                  required
                  value={values.difficulty}
                  onChange={(value) => handleChange('difficulty', value)}
                  onBlur={() => handleBlur('difficulty')}
                  error={errors.difficulty}
                >
                  <option value="">Select difficulty</option>
                  <option value="beginner">Beginner</option>
                  <option value="intermediate">Intermediate</option>
                  <option value="advanced">Advanced</option>
                </FormField>
              </div>
              <div className="flex gap-2 mt-6">
                <Button type="submit" disabled={isSubmitting || courseMutationsLoading}>
                  {isSubmitting || courseMutationsLoading ? 'Creating...' : 'Create Course'}
                </Button>
                <Button 
                  type="button" 
                  variant="outline" 
                  onClick={() => {
                    setShowCreateForm(false);
                    resetForm();
                  }}
                >
                  Cancel
                </Button>
              </div>
            </form>
          </CardContent>
        </Card>
      )}
    </div>
  );
};

export default RealtimeCourseManagement;

import React, { useState } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '../ui/Card';
import Badge from '../ui/Badge';
import Button from '../ui/Button';
import { Alert, AlertDescription, AlertIcon } from '../ui/Alert';
import { useRealtime } from '../../contexts/RealtimeContext';
import { 
  Users, 
  BookOpen, 
  CreditCard, 
  TrendingUp, 
  Clock, 
  CheckCircle,
  AlertCircle,
  RefreshCw
} from 'lucide-react';
import { cn } from '../../utils/cn';

const RealtimeDashboard = () => {
  const {
    adminStats,
    adminEnrollments,
    adminPayments,
    courses,
    coupons,
    coursesLoading,
    adminEnrollmentsLoading,
    adminPaymentsLoading,
    coursesError,
    adminEnrollmentsError,
    adminPaymentsError,
    isConnected,
    lastUpdated,
    getRecentEnrollments,
    getRecentPayments,
    getActiveCoupons
  } = useRealtime();

  const [activeTab, setActiveTab] = useState('overview');

  if (coursesError || adminEnrollmentsError || adminPaymentsError) {
    return (
      <Alert variant="destructive">
        <AlertIcon variant="destructive" />
        <AlertDescription>
          Error loading dashboard data. Please check your connection and try again.
        </AlertDescription>
      </Alert>
    );
  }

  const recentEnrollments = getRecentEnrollments(5);
  const recentPayments = getRecentPayments(5);
  const activeCoupons = getActiveCoupons();

  const formatCurrency = (amount) => {
    return new Intl.NumberFormat('en-IN', {
      style: 'currency',
      currency: 'INR',
      minimumFractionDigits: 0
    }).format(amount / 100);
  };

  const formatDate = (timestamp) => {
    if (!timestamp) return 'N/A';
    return new Date(timestamp.toDate ? timestamp.toDate() : timestamp).toLocaleDateString();
  };

  const StatCard = ({ title, value, icon: Icon, trend, color = 'blue' }) => (
    <Card>
      <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
        <CardTitle className="text-sm font-medium">{title}</CardTitle>
        <Icon className={cn('h-4 w-4', `text-${color}-600`)} />
      </CardHeader>
      <CardContent>
        <div className="text-2xl font-bold">{value}</div>
        {trend && (
          <p className="text-xs text-muted-foreground flex items-center gap-1">
            <TrendingUp className="h-3 w-3" />
            {trend}
          </p>
        )}
      </CardContent>
    </Card>
  );

  return (
    <div className="space-y-6">
      {/* Connection Status */}
      <div className="flex items-center justify-between">
        <div className="flex items-center gap-2">
          <Badge variant={isConnected ? 'success' : 'destructive'}>
            {isConnected ? (
              <>
                <CheckCircle className="h-3 w-3 mr-1" />
                Connected
              </>
            ) : (
              <>
                <AlertCircle className="h-3 w-3 mr-1" />
                Disconnected
              </>
            )}
          </Badge>
          <span className="text-sm text-muted-foreground">
            Last updated: {new Date(lastUpdated).toLocaleTimeString()}
          </span>
        </div>
        <Button variant="outline" size="sm">
          <RefreshCw className="h-4 w-4 mr-2" />
          Refresh
        </Button>
      </div>

      {/* Stats Overview */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
        <StatCard
          title="Total Users"
          value={adminStats?.totalUsers || 0}
          icon={Users}
          color="blue"
        />
        <StatCard
          title="Total Courses"
          value={adminStats?.totalCourses || 0}
          icon={BookOpen}
          color="green"
        />
        <StatCard
          title="Total Enrollments"
          value={adminStats?.totalEnrollments || 0}
          icon={TrendingUp}
          color="purple"
        />
        <StatCard
          title="Total Revenue"
          value={formatCurrency(adminStats?.totalRevenue || 0)}
          icon={CreditCard}
          color="orange"
        />
      </div>

      {/* Tabs */}
      <div className="flex space-x-1 bg-muted p-1 rounded-lg">
        {[
          { id: 'overview', label: 'Overview' },
          { id: 'enrollments', label: 'Recent Enrollments' },
          { id: 'payments', label: 'Recent Payments' },
          { id: 'coupons', label: 'Active Coupons' }
        ].map((tab) => (
          <Button
            key={tab.id}
            variant={activeTab === tab.id ? 'default' : 'ghost'}
            size="sm"
            onClick={() => setActiveTab(tab.id)}
            className="flex-1"
          >
            {tab.label}
          </Button>
        ))}
      </div>

      {/* Tab Content */}
      {activeTab === 'overview' && (
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
          {/* Recent Enrollments */}
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <Clock className="h-5 w-5" />
                Recent Enrollments
              </CardTitle>
            </CardHeader>
            <CardContent>
              {adminEnrollmentsLoading ? (
                <div className="text-center py-4">Loading...</div>
              ) : recentEnrollments.length > 0 ? (
                <div className="space-y-3">
                  {recentEnrollments.map((enrollment) => (
                    <div key={enrollment.id} className="flex items-center justify-between p-3 bg-muted rounded-lg">
                      <div>
                        <p className="font-medium">{enrollment.courseTitle}</p>
                        <p className="text-sm text-muted-foreground">
                          {enrollment.userId}
                        </p>
                      </div>
                      <div className="text-right">
                        <Badge variant="success">{enrollment.status}</Badge>
                        <p className="text-xs text-muted-foreground mt-1">
                          {formatDate(enrollment.enrolledAt)}
                        </p>
                      </div>
                    </div>
                  ))}
                </div>
              ) : (
                <p className="text-center text-muted-foreground py-4">
                  No recent enrollments
                </p>
              )}
            </CardContent>
          </Card>

          {/* Recent Payments */}
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <CreditCard className="h-5 w-5" />
                Recent Payments
              </CardTitle>
            </CardHeader>
            <CardContent>
              {adminPaymentsLoading ? (
                <div className="text-center py-4">Loading...</div>
              ) : recentPayments.length > 0 ? (
                <div className="space-y-3">
                  {recentPayments.map((payment) => (
                    <div key={payment.id} className="flex items-center justify-between p-3 bg-muted rounded-lg">
                      <div>
                        <p className="font-medium">{payment.courseTitle}</p>
                        <p className="text-sm text-muted-foreground">
                          {payment.userId}
                        </p>
                      </div>
                      <div className="text-right">
                        <p className="font-medium">{formatCurrency(payment.amount)}</p>
                        <Badge 
                          variant={payment.status === 'captured' ? 'success' : 'secondary'}
                        >
                          {payment.status}
                        </Badge>
                      </div>
                    </div>
                  ))}
                </div>
              ) : (
                <p className="text-center text-muted-foreground py-4">
                  No recent payments
                </p>
              )}
            </CardContent>
          </Card>
        </div>
      )}

      {activeTab === 'enrollments' && (
        <Card>
          <CardHeader>
            <CardTitle>All Recent Enrollments</CardTitle>
          </CardHeader>
          <CardContent>
            {adminEnrollmentsLoading ? (
              <div className="text-center py-8">Loading enrollments...</div>
            ) : adminEnrollments.length > 0 ? (
              <div className="space-y-3">
                {adminEnrollments.map((enrollment) => (
                  <div key={enrollment.id} className="flex items-center justify-between p-4 border rounded-lg">
                    <div className="flex-1">
                      <h4 className="font-medium">{enrollment.courseTitle}</h4>
                      <p className="text-sm text-muted-foreground">
                        User: {enrollment.userId}
                      </p>
                      <p className="text-sm text-muted-foreground">
                        Enrolled: {formatDate(enrollment.enrolledAt)}
                      </p>
                    </div>
                    <div className="text-right">
                      <Badge variant="success">{enrollment.status}</Badge>
                      <p className="text-sm font-medium mt-1">
                        {formatCurrency(enrollment.amount || 0)}
                      </p>
                    </div>
                  </div>
                ))}
              </div>
            ) : (
              <p className="text-center text-muted-foreground py-8">
                No enrollments found
              </p>
            )}
          </CardContent>
        </Card>
      )}

      {activeTab === 'payments' && (
        <Card>
          <CardHeader>
            <CardTitle>All Recent Payments</CardTitle>
          </CardHeader>
          <CardContent>
            {adminPaymentsLoading ? (
              <div className="text-center py-8">Loading payments...</div>
            ) : adminPayments.length > 0 ? (
              <div className="space-y-3">
                {adminPayments.map((payment) => (
                  <div key={payment.id} className="flex items-center justify-between p-4 border rounded-lg">
                    <div className="flex-1">
                      <h4 className="font-medium">{payment.courseTitle}</h4>
                      <p className="text-sm text-muted-foreground">
                        User: {payment.userId}
                      </p>
                      <p className="text-sm text-muted-foreground">
                        Payment ID: {payment.paymentId}
                      </p>
                    </div>
                    <div className="text-right">
                      <p className="text-lg font-bold">{formatCurrency(payment.amount)}</p>
                      <Badge 
                        variant={payment.status === 'captured' ? 'success' : 'secondary'}
                      >
                        {payment.status}
                      </Badge>
                      <p className="text-sm text-muted-foreground mt-1">
                        {formatDate(payment.createdAt)}
                      </p>
                    </div>
                  </div>
                ))}
              </div>
            ) : (
              <p className="text-center text-muted-foreground py-8">
                No payments found
              </p>
            )}
          </CardContent>
        </Card>
      )}

      {activeTab === 'coupons' && (
        <Card>
          <CardHeader>
            <CardTitle>Active Coupons</CardTitle>
          </CardHeader>
          <CardContent>
            {couponsLoading ? (
              <div className="text-center py-8">Loading coupons...</div>
            ) : activeCoupons.length > 0 ? (
              <div className="space-y-3">
                {activeCoupons.map((coupon) => (
                  <div key={coupon.id} className="flex items-center justify-between p-4 border rounded-lg">
                    <div className="flex-1">
                      <h4 className="font-medium">{coupon.name}</h4>
                      <p className="text-sm text-muted-foreground">
                        Code: {coupon.code}
                      </p>
                      <p className="text-sm text-muted-foreground">
                        {coupon.type === 'percent' ? `${coupon.value}%` : formatCurrency(coupon.value)} discount
                      </p>
                    </div>
                    <div className="text-right">
                      <Badge variant="success">Active</Badge>
                      <p className="text-sm text-muted-foreground mt-1">
                        Used: {coupon.usedCount}/{coupon.usageLimit || '∞'}
                      </p>
                      <p className="text-sm text-muted-foreground">
                        Valid until: {formatDate(coupon.validUntil)}
                      </p>
                    </div>
                  </div>
                ))}
              </div>
            ) : (
              <p className="text-center text-muted-foreground py-8">
                No active coupons
              </p>
            )}
          </CardContent>
        </Card>
      )}
    </div>
  );
};

export default RealtimeDashboard;

import React from 'react';
import { Navigate } from 'react-router-dom';
import { useAuth } from '../../contexts/AuthContext.jsx';

const ProtectedRoute = ({ children, requiredRole }) => {
  const { isAuthenticated, isAdmin, loading } = useAuth();

  if (loading) return <div className="p-8 text-center">Loading user status...</div>;
  if (!isAuthenticated) return <Navigate to="/auth/signin" replace />;
  if (requiredRole === 'admin' && !isAdmin) return <Navigate to="/" replace />;
  return children;
};

export default ProtectedRoute;

import React from 'react';
import { Link } from 'react-router-dom';
import { Clock, Globe, Star, Award, CheckCircle, Edit } from 'lucide-react';
import { Card, CardContent, CardFooter, CardHeader, CardTitle } from '../ui/Card';
import Badge from '../ui/Badge';
import Button from '../ui/Button';
import { cn } from '../../utils/cn';

const CourseCard = ({
  course,
  isEnrolled = false,
  showAdminOptions = false,
  className,
  ...props
}) => {
  // Price formatting
  const price = Number(course.price) || 0;
  const originalPrice = Number(course.originalPrice) || (price + 2000);

  // Status color mapping for admin view
  const getStatusVariant = (status) => {
    switch (status) {
      case 'published': return 'success';
      case 'draft': return 'warning';
      case 'archived': return 'destructive';
      default: return 'secondary';
    }
  };

  return (
    <Card className={cn('group hover:shadow-lg transition-all duration-300 hover:-translate-y-1', className)} {...props}>
      {/* Course Image */}
      <div className="relative h-40 overflow-hidden rounded-t-lg">
        <img
          src={course.imageUrl || "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSUwCJYSnbBLMEGWKfSnWRGC_34iCCKkxePpg&s"}
          alt={course.title}
          className="w-full h-full object-cover transition-transform duration-500 group-hover:scale-105"
        />

        {/* Admin Status Badge */}
        {showAdminOptions && course.status && (
          <Badge 
            variant={getStatusVariant(course.status)}
            className="absolute top-3 left-3 z-10"
          >
            {course.status}
          </Badge>
        )}

        {/* Public Badges */}
        {course.isBestseller && (
          <Badge 
            variant="success"
            className="absolute top-3 left-3 text-white bg-green-600 hover:bg-green-700"
          >
            <Award size={14} className="mr-1" />
            Bestseller
          </Badge>
        )}

        {/* Rating Badge */}
        <div className="absolute top-3 right-3 bg-black/60 text-white text-sm px-2 py-1 rounded-lg flex items-center font-semibold">
          <Star size={14} className="fill-yellow-400 text-yellow-400 mr-1" />
          {course.rating || '4.5'}
        </div>
      </div>

      <CardHeader className="pb-3">
        <CardTitle className="text-lg leading-snug line-clamp-2">
          {course.title}
        </CardTitle>
      </CardHeader>

      <CardContent className="pt-0 pb-3">
        {/* Course Details */}
