};

// src/contexts/LearnPageContext.jsx (Previously CourseContentContext.jsx)

import React, { createContext, useContext, useState, useEffect, useMemo, useCallback } from 'react';
import { 
    getCourseContent, 
    getUserProgress, 
    updateUserProgress,
    checkUserEnrollment,
    getSecureVideoAccessUrl 
} from '../firebase/services'; // Assuming services are defined
import { useAuth } from './AuthContext';

// 🚨 FIX 1: Rename the context constant here to match the Provider/Hook names
const LearnPageContext = createContext(undefined); 

// Using a mock increment since we can't import from firebase/firestore here
const increment = (value) => value; 

// 🚨 FALLBACK IMPORTS (Add these based on your file structure)
import { EMERGING_TECH_COURSE_CONTENT, FALLBACK_ENROLLMENT_STATUS, FALLBACK_COURSE_ID } from '../utils/fallbackData'; 


export const useLearnPage = () => {
    const ctx = useContext(LearnPageContext); // Use the correct context name here
    if (!ctx) throw new Error('useLearnPage must be used within LearnPageProvider');
    return ctx;
};

// 🚨 FIX 2: Rename the provider component to match the file and import structure
export const LearnPageProvider = ({ children }) => {
    const { currentUser, isAuthenticated } = useAuth();
    
    const [courseContent, setCourseContent] = useState([]);
    const [currentModule, setCurrentModule] = useState(null);
    const [loadingContent, setLoadingContent] = useState(false);
    const [contentError, setContentError] = useState(null);
    
    const [userProgress, setUserProgress] = useState(null);
    const [loadingProgress, setLoadingProgress] = useState(false);
    const [progressError, setProgressError] = useState(null);
    
    const [enrollmentStatus, setEnrollmentStatus] = useState({
        isEnrolled: false,
        enrollment: null,
        loading: false,
        error: null
    });

    // Map backend content to secure, playable URLs by validating enrollment per video
    const attachSecureUrls = useCallback(async (courseId, modules) => {
        if (!Array.isArray(modules) || modules.length === 0) return [];
        const mapped = await Promise.all(modules.map(async (mod) => {
            const videos = Array.isArray(mod.videos) ? mod.videos : [];
            const securedVideos = await Promise.all(videos.map(async (v) => {
                // Prefer existing url for public/fallback; otherwise fetch secure url via backend gate
                if (v.url) return v;
                if (v.videoKey) {
                    const res = await getSecureVideoAccessUrl(currentUser?.uid, courseId, v.videoKey);
                    if (res?.success && res.data?.signedUrl) {
                        return { ...v, url: res.data.signedUrl };
                    }
                }
                return v;
            }));
            return { ...mod, videos: securedVideos };
        }));
        return mapped;
    }, [currentUser?.uid]);

    // --- Core Enrollment Check with Fallback (Logic kept the same) ---
    const checkEnrollment = useCallback(async (courseId) => {
        if (!courseId || !currentUser?.uid) {
            return { isEnrolled: false, error: null };
        }

        try {
            const result = await checkUserEnrollment(currentUser.uid, courseId);
            if (result.success) {
                const status = { isEnrolled: result.data.isEnrolled, enrollment: result.data.enrollment, error: null };
                setEnrollmentStatus(status);
                return status;
            } else {
                throw new Error(result.error || 'Enrollment check failed.');
            }
        } catch (err) {
            console.error('Failed to check enrollment from DB:', err);
            
            // 🚨 FALLBACK ENROLLMENT 🚨
            if (courseId === FALLBACK_COURSE_ID) {
                setEnrollmentStatus(FALLBACK_ENROLLMENT_STATUS);
                return FALLBACK_ENROLLMENT_STATUS;
            } else {
                const status = { isEnrolled: false, enrollment: null, error: 'Failed to check enrollment status' };
                setEnrollmentStatus(status);
                return status;
            }
        }
    }, [currentUser?.uid]);

    // --- Core Content Fetch with Enrollment Gate and Fallback (Logic kept the same) ---
    const fetchCourseContent = useCallback(async (courseId) => {
        if (!courseId || !isAuthenticated || !currentUser) {
            setLoadingContent(false);
            return;
        }

        setLoadingContent(true);
        setContentError(null);
        
        const currentEnrollmentStatus = await checkEnrollment(courseId);

        if (!currentEnrollmentStatus.isEnrolled) {
            setLoadingContent(false);
            return; 
        }

        try {
            // Force fallback (offline mode)
            if (courseId === FALLBACK_COURSE_ID && currentEnrollmentStatus.isEnrolled) {
                setCourseContent(EMERGING_TECH_COURSE_CONTENT);
                setCurrentModule(EMERGING_TECH_COURSE_CONTENT[0]);
            } else {
                setCourseContent([]);
                setCurrentModule(null);
            }
        } finally {
            setLoadingContent(false);
        }
    }, [isAuthenticated, currentUser, checkEnrollment, currentModule]); 
    
    // Fetch user progress for a course (Logic kept the same)
    const fetchUserProgress = useCallback(async (courseId) => {
        if (!courseId || !currentUser?.uid) return;

        try {
            setLoadingProgress(true);
            setProgressError(null);
            const result = await getUserProgress(currentUser.uid, courseId);
            if (result.success) {
                setUserProgress(result.data);
            } else {
                setProgressError(result.error);
                setUserProgress(null);
            }
        } catch (err) {
            console.error('Failed to fetch user progress:', err);
            setProgressError('Failed to load learning progress.');
            setUserProgress(null);
        } finally {
            setLoadingProgress(false);
        }
    }, [currentUser?.uid]);


    // Update Progress and Getter stubs (Logic kept the same)
    const updateProgress = useCallback(async (courseId, progressData) => { return { success: true }; }, [currentUser?.uid, fetchUserProgress]);
    const markVideoWatched = useCallback(async (courseId, moduleId, videoId, watchedDuration, totalDuration) => updateProgress(courseId, {}), [updateProgress]);
    const markModuleCompleted = useCallback(async (courseId, moduleId) => updateProgress(courseId, {}), [updateProgress]);
    const getModuleProgress = useCallback((moduleId) => { /* ... */ return null; }, [userProgress]); 
    const getVideoProgress = useCallback((moduleId, videoId) => { /* ... */ return null; }, [getModuleProgress]);
    const isModuleUnlocked = useCallback((module, index) => { /* ... */ return true; }, [courseContent, getModuleProgress]);
    const clearCourseData = useCallback(() => { /* ... */ }, []);

    // Fetch progress when content loads/user changes
    useEffect(() => {
        if (isAuthenticated && currentUser && currentModule) {
             const id = currentModule.courseId || FALLBACK_COURSE_ID; 
             fetchUserProgress(id);
        }
    }, [isAuthenticated, currentUser, currentModule, fetchUserProgress]); 


    const value = useMemo(() => ({
        courseContent,
        currentModule,
        loadingContent: loadingContent || enrollmentStatus.loading,
        contentError,
        fetchCourseContent,
        setCurrentModule,
        markVideoWatched,
        markModuleCompleted,
        getModuleProgress,
        getVideoProgress,
        isModuleUnlocked,
        enrollmentStatus,
        // Computed values (placeholders)
        completionPercentage: 25, 
        timeSpent: 1200, 
    }), [
        courseContent, currentModule, loadingContent, contentError, 
        userProgress, loadingProgress, enrollmentStatus, isModuleUnlocked, 
        fetchCourseContent, setCurrentModule, markVideoWatched, markModuleCompleted,
        getModuleProgress, getVideoProgress
    ]);

    return (
        // 🚨 FIX: Use LearnPageContext in the Provider tag
        <LearnPageContext.Provider value={value}>
            {children}
        </LearnPageContext.Provider>
    );
};

import React, { createContext, useContext, useState, useEffect, useMemo } from 'react';
import { 
  getUserPaymentHistory, 
  validateCouponCode, 
  applyCoupon,
  getAllActiveCoupons 
} from '../firebase/services';
import { useAuth } from './AuthContext';

const PaymentContext = createContext(undefined);

export const usePayment = () => {
  const ctx = useContext(PaymentContext);
  if (!ctx) throw new Error('usePayment must be used within PaymentProvider');
  return ctx;
};

export const PaymentProvider = ({ children }) => {
  const { currentUser, isAuthenticated } = useAuth();
  
  // Payment history state
  const [paymentHistory, setPaymentHistory] = useState([]);
  const [loadingPayments, setLoadingPayments] = useState(false);
  const [paymentsError, setPaymentsError] = useState(null);
  
  // Coupon state
  const [availableCoupons, setAvailableCoupons] = useState([]);
  const [loadingCoupons, setLoadingCoupons] = useState(false);
  const [couponsError, setCouponsError] = useState(null);
  
  // Current coupon validation state
  const [couponValidation, setCouponValidation] = useState({
    isValid: false,
    coupon: null,
    error: null,
    loading: false
  });

  // Fetch user payment history
  const fetchPaymentHistory = async (uid) => {
    if (!uid) return setPaymentHistory([]);

    try {
      setLoadingPayments(true);
      setPaymentsError(null);

      const result = await getUserPaymentHistory(uid);
      if (result.success) {
        setPaymentHistory(result.data);
      } else {
        setPaymentsError(result.error);
        setPaymentHistory([]);
      }
    } catch (err) {
      console.error('Failed to fetch payment history:', err);
      setPaymentsError('Failed to load payment history.');
      setPaymentHistory([]);
    } finally {
      setLoadingPayments(false);
    }
  };

  // Fetch available coupons
  const fetchAvailableCoupons = async () => {
    try {
      setLoadingCoupons(true);
      setCouponsError(null);

      const result = await getAllActiveCoupons();
      if (result.success) {
        setAvailableCoupons(result.data);
      } else {
        setCouponsError(result.error);
        setAvailableCoupons([]);
      }
    } catch (err) {
      console.error('Failed to fetch coupons:', err);
      setCouponsError('Failed to load available coupons.');
      setAvailableCoupons([]);
    } finally {
      setLoadingCoupons(false);
    }
  };

  // Validate coupon code
  const validateCoupon = async (couponCode, courseId) => {
    if (!couponCode || !courseId) {
      setCouponValidation({
        isValid: false,
        coupon: null,
        error: 'Coupon code and course ID are required',
        loading: false
      });
      return;
    }

    try {
      setCouponValidation(prev => ({ ...prev, loading: true, error: null }));

      const result = await validateCouponCode(couponCode, courseId, currentUser?.uid);
      
      if (result.success) {
        setCouponValidation({
          isValid: true,
          coupon: result.data,
          error: null,
          loading: false
        });
      } else {
        setCouponValidation({
          isValid: false,
          coupon: null,
          error: result.error,
          loading: false
        });
      }
    } catch (err) {
      console.error('Failed to validate coupon:', err);
      setCouponValidation({
        isValid: false,
        coupon: null,
        error: 'Failed to validate coupon code',
        loading: false
      });
    }
  };

  // Apply coupon (this would typically be called during checkout)
  const applyCouponToOrder = async (couponCode, courseId) => {
    if (!couponCode || !courseId) {
      return { success: false, error: 'Coupon code and course ID are required' };
    }

    try {
      const result = await applyCoupon(couponCode, courseId, currentUser?.uid);
      return result;
    } catch (err) {
      console.error('Failed to apply coupon:', err);
      return { success: false, error: 'Failed to apply coupon' };
    }
  };

  // Clear coupon validation
  const clearCouponValidation = () => {
    setCouponValidation({
      isValid: false,
      coupon: null,
      error: null,
      loading: false
    });
  };

  // Calculate discount amount
  const calculateDiscount = (coupon, subtotal) => {
    if (!coupon || !coupon.isValid) return 0;

    const { type, value, maxDiscountAmount } = coupon.coupon;
    
    let discount = 0;
    
    if (type === 'percent') {
      discount = Math.min(subtotal * (value / 100), maxDiscountAmount || subtotal);
    } else if (type === 'flat') {
      discount = Math.min(value, subtotal);
    }
    
    return Math.round(discount);
  };

  // Effect to fetch payment history when user changes
  useEffect(() => {
    if (isAuthenticated && currentUser?.uid) {
      fetchPaymentHistory(currentUser.uid);
    } else {
      setPaymentHistory([]);
      setLoadingPayments(false);
    }
  }, [isAuthenticated, currentUser?.uid]);

  // Effect to fetch available coupons on mount
  useEffect(() => {
    fetchAvailableCoupons();
  }, []);

  const value = useMemo(() => ({
    // Payment history
    paymentHistory,
    loadingPayments,
    paymentsError,
    refreshPaymentHistory: () => fetchPaymentHistory(currentUser?.uid),
    
    // Coupons
    availableCoupons,
    loadingCoupons,
    couponsError,
    refreshCoupons: fetchAvailableCoupons,
    
    // Coupon validation
    couponValidation,
    validateCoupon,
    applyCouponToOrder,
    clearCouponValidation,
    calculateDiscount,
    
    // Helper functions
    getPaymentById: (paymentId) => paymentHistory.find(p => p.id === paymentId),
    getPaymentsByCourse: (courseId) => paymentHistory.filter(p => p.courseId === courseId),
    getTotalSpent: () => paymentHistory.reduce((total, payment) => {
      return total + (payment.status === 'captured' ? payment.amount : 0);
    }, 0),
  }), [
    paymentHistory,
    loadingPayments,
    paymentsError,
    availableCoupons,
    loadingCoupons,
    couponsError,
    couponValidation,
    currentUser?.uid,
    isAuthenticated
  ]);

  return (
    <PaymentContext.Provider value={value}>
      {children}
    </PaymentContext.Provider>
  );
};

import React, { createContext, useContext, useMemo, useCallback } from 'react';
import { useAuth } from './AuthContext';
import {
  useRealtimeCourses,
  useRealtimeUserEnrollments,
  useRealtimeAdminUsers,
  useRealtimeAdminEnrollments,
  useRealtimeAdminPayments,
  useRealtimeCoupons,
  useRealtimeCourseMutations,
  useRealtimeEnrollmentMutations
} from '../hooks/useRealtimeFirebase';

const RealtimeContext = createContext(undefined);

export const useRealtime = () => {
  const ctx = useContext(RealtimeContext);
  if (!ctx) throw new Error('useRealtime must be used within RealtimeProvider');
  return ctx;
};

export const RealtimeProvider = ({ children }) => {
  const { currentUser, isAuthenticated, isAdmin } = useAuth();

  // ============================================================================
  // REAL-TIME DATA HOOKS
  // ============================================================================

  // Courses data
  const { 
    data: courses, 
    loading: coursesLoading, 
    error: coursesError 
  } = useRealtimeCourses({ 
    publishedOnly: true, 
    limitCount: 50 
  });

  // User enrollments
  const { 
    enrollments, 
    loading: enrollmentsLoading, 
    error: enrollmentsError, 
    isEnrolled,
    enrollmentCount 
  } = useRealtimeUserEnrollments(currentUser?.uid, { 
    enabled: isAuthenticated 
  });

  // Admin data (only load if user is admin)
  const { 
    data: adminUsers, 
    loading: adminUsersLoading, 
    error: adminUsersError 
  } = useRealtimeAdminUsers({ 
    enabled: isAdmin 
  });

  const { 
    data: adminEnrollments, 
    loading: adminEnrollmentsLoading, 
    error: adminEnrollmentsError 
  } = useRealtimeAdminEnrollments({ 
    enabled: isAdmin 
  });

  const { 
    data: adminPayments, 
    loading: adminPaymentsLoading, 
    error: adminPaymentsError 
  } = useRealtimeAdminPayments({ 
    enabled: isAdmin 
  });

  // Coupons data
  const { 
    data: coupons, 
    loading: couponsLoading, 
    error: couponsError 
  } = useRealtimeCoupons({ 
    activeOnly: true 
  });

  // ============================================================================
  // REAL-TIME MUTATION HOOKS
  // ============================================================================

  const { 
    updateCourse, 
    createCourse, 
    loading: courseMutationsLoading, 
    error: courseMutationsError 
  } = useRealtimeCourseMutations();

  const { 
    createEnrollment, 
    updateEnrollmentProgress, 
    loading: enrollmentMutationsLoading, 
    error: enrollmentMutationsError 
  } = useRealtimeEnrollmentMutations();

  // ============================================================================
  // COMPUTED VALUES
  // ============================================================================

  const adminStats = useMemo(() => {
    if (!isAdmin) return null;

    const totalUsers = adminUsers?.length || 0;
    const totalEnrollments = adminEnrollments?.length || 0;
    const totalRevenue = adminPayments?.reduce((sum, payment) => {
      return sum + (payment.status === 'captured' ? payment.amount : 0);
    }, 0) || 0;
    const totalCourses = courses?.length || 0;

    return {
      totalUsers,
      totalEnrollments,
      totalRevenue,
      totalCourses,
      activeCoupons: coupons?.length || 0
    };
  }, [isAdmin, adminUsers, adminEnrollments, adminPayments, courses, coupons]);

  const userStats = useMemo(() => {
    if (!isAuthenticated) return null;

    return {
      enrolledCourses: enrollmentCount,
      completedCourses: enrollments?.filter(e => e.progress?.completionPercentage >= 100).length || 0,
      totalSpent: enrollments?.reduce((sum, enrollment) => {
        return sum + (enrollment.amount || 0);
      }, 0) || 0
    };
  }, [isAuthenticated, enrollments, enrollmentCount]);

  // ============================================================================
  // HELPER FUNCTIONS
  // ============================================================================

  const getCourseById = useCallback((courseId) => {
    return courses?.find(c => String(c.id) === String(courseId));
  }, [courses]);

  const getUserEnrollmentsByCourse = useCallback((courseId) => {
    return enrollments?.filter(e => String(e.courseId) === String(courseId));
  }, [enrollments]);

  const getRecentEnrollments = useCallback((limit = 10) => {
    return adminEnrollments?.slice(0, limit) || [];
  }, [adminEnrollments]);

  const getRecentPayments = useCallback((limit = 10) => {
    return adminPayments?.slice(0, limit) || [];
  }, [adminPayments]);

  const getActiveCoupons = useCallback(() => {
    return coupons?.filter(c => c.isActive && new Date(c.validUntil?.toDate()) > new Date()) || [];
  }, [coupons]);

  // ============================================================================
  // CONTEXT VALUE
  // ============================================================================

  const value = useMemo(() => ({
    // Real-time data
    courses,
    enrollments,
    adminUsers,
    adminEnrollments,
    adminPayments,
    coupons,

    // Loading states
    coursesLoading,
    enrollmentsLoading,
    adminUsersLoading,
    adminEnrollmentsLoading,
    adminPaymentsLoading,
    couponsLoading,
    courseMutationsLoading,
    enrollmentMutationsLoading,

    // Error states
    coursesError,
    enrollmentsError,
    adminUsersError,
    adminEnrollmentsError,
    adminPaymentsError,
    couponsError,
    courseMutationsError,
    enrollmentMutationsError,

    // Computed values
    adminStats,
    userStats,

    // Helper functions
    isEnrolled,
    getCourseById,
    getUserEnrollmentsByCourse,
    getRecentEnrollments,
    getRecentPayments,
    getActiveCoupons,

    // Mutations
    updateCourse,
    createCourse,
    createEnrollment,
    updateEnrollmentProgress,

    // Status
    isConnected: !coursesError && !enrollmentsError,
    lastUpdated: new Date().toISOString()
  }), [
    courses,
    enrollments,
    adminUsers,
    adminEnrollments,
    adminPayments,
    coupons,
    coursesLoading,
    enrollmentsLoading,
    adminUsersLoading,
    adminEnrollmentsLoading,
    adminPaymentsLoading,
    couponsLoading,
    courseMutationsLoading,
    enrollmentMutationsLoading,
    coursesError,
    enrollmentsError,
    adminUsersError,
    adminEnrollmentsError,
    adminPaymentsError,
    couponsError,
    courseMutationsError,
    enrollmentMutationsError,
    adminStats,
    userStats,
    isEnrolled,
    getCourseById,
    getUserEnrollmentsByCourse,
    getRecentEnrollments,
    getRecentPayments,
    getActiveCoupons,
    updateCourse,
    createCourse,
    createEnrollment,
    updateEnrollmentProgress
  ]);

  return (
    <RealtimeContext.Provider value={value}>
      {children}
    </RealtimeContext.Provider>
  );
};

import React, { createContext, useContext, useEffect, useMemo, useState, useCallback } from 'react';

const STORAGE_KEY = 'app_theme';
const DEFAULT_THEME = 'light';

const ThemeContext = createContext(undefined);

export const useTheme = () => {
    const ctx = useContext(ThemeContext);
    if (!ctx) throw new Error('useTheme must be used within ThemeProvider');
    return ctx;
};

export const ThemeProvider = ({ children }) => {
    const [theme, setTheme] = useState(DEFAULT_THEME);

    useEffect(() => {
        const saved = typeof window !== 'undefined' ? window.localStorage.getItem(STORAGE_KEY) : null;
        if (saved === 'light' || saved === 'dark') {
            setTheme(saved);
        } else {
            setTheme(DEFAULT_THEME);
        }
    }, []);

    useEffect(() => {
        if (typeof document !== 'undefined') {
            const root = document.documentElement;
            if (theme === 'dark') {
                root.setAttribute('data-theme', 'dark');
            } else {
                root.removeAttribute('data-theme');
            }
        }
        if (typeof window !== 'undefined') {
            window.localStorage.setItem(STORAGE_KEY, theme);
        }
    }, [theme]);

    const toggleTheme = useCallback(() => {
        setTheme(prev => (prev === 'light' ? 'dark' : 'light'));
    }, []);

    const value = useMemo(() => ({ theme, setTheme, toggleTheme, isDark: theme === 'dark' }), [theme, toggleTheme]);

    return (
        <ThemeContext.Provider value={value}>
            {children}
        </ThemeContext.Provider>
    );
};

export default ThemeContext;

