import { useState, useCallback } from "react";
import { createEnrollmentWithPayment } from "../firebase/services";
import { RAZORPAY_KEY_ID } from "../firebase";

/**
 * Custom hook to manage the Razorpay checkout process.
 * It loads the Razorpay SDK script and provides a payment initiation function.
 * @param {object} currentUser - The current Firebase user object (from useAuth).
 * @param {function} onPaymentSuccess - Callback function to run after successful payment confirmation.
 */
const useRazorpay = (currentUser, onPaymentSuccess) => {
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);

  // Dynamic Script Loading Function
  const loadScript = useCallback((src) => {
    return new Promise((resolve) => {
      const script = document.createElement("script");
      script.src = src;
      script.onload = () => resolve(true);
      script.onerror = () => resolve(false);
      document.body.appendChild(script);
    });
  }, []);

  // Main Payment Initiation Function
  const initializePayment = useCallback(
    async (paymentDetails) => {
      if (!currentUser || !RAZORPAY_KEY_ID) {
        setError("Authentication or Razorpay Key is missing.");
        return false;
      }

      setIsLoading(true);
      setError(null);

      // Ensure Razorpay script is loaded
      const res = await loadScript(
        "https://checkout.razorpay.com/v1/checkout.js"
      );

      if (!res) {
        setError(
          "Razorpay SDK failed to load. Are you connected to the internet?"
        );
        setIsLoading(false);
        return false;
      }

      const amountInPaise = Math.round(paymentDetails.amount * 100);

      const options = {
        key: RAZORPAY_KEY_ID,
        amount: amountInPaise,
        currency: "INR",
        name: "JNTU-GV Certification",
        description: `Enrollment for ${paymentDetails.courseTitle}`,
        image: "YOUR_COURSE_LOGO_URL",
        order_id: paymentDetails.orderId || "",

        handler: async (response) => {
          try {
            // Create enrollment + payment record in Firestore using batch operation
            const result = await createEnrollmentWithPayment(
              {
                userId: currentUser.uid,
                courseId: paymentDetails.courseId,
                courseTitle: paymentDetails.courseTitle,
                status: "SUCCESS",
                paymentData: {
                  paymentId: response.razorpay_payment_id,
                  amount: paymentDetails.amount,
                },
              },
              {
                userId: currentUser.uid,
                courseId: paymentDetails.courseId,
                courseTitle: paymentDetails.courseTitle,
                amount: paymentDetails.amount,
                currency: "INR",
                status: "captured",
                razorpayData: {
                  paymentId: response.razorpay_payment_id,
                  orderId: response.razorpay_order_id,
                  signature: response.razorpay_signature,
                },
              }
            );

            if (result.success) {
              // Execute the success callback provided by the component
              if (onPaymentSuccess) {
                onPaymentSuccess(result.data.enrollmentId, paymentDetails.courseId);
              }
            } else {
              setError("Payment successful, but failed to record enrollment. Please contact support.");
            }
          } catch (err) {
            setError(
              "Payment successful, but failed to record enrollment. Please contact support immediately."
            );
            console.error("Firestore Enrollment Error:", err);
          } finally {
            setIsLoading(false);
          }
        },

        // Pre-fill user details for better conversion
        prefill: {
          name: paymentDetails.billingInfo?.name || currentUser.displayName || "",
          email: paymentDetails.billingInfo?.email || currentUser.email || "",
          contact: paymentDetails.billingInfo?.phone || currentUser.phoneNumber || "",
        },

        theme: {
          color: "#004080",
        },
      };

      const rzp1 = new window.Razorpay(options);

      // Handle payment failure event
      rzp1.on("payment.failed", function (response) {
        setError(
          `Payment failed. Code: ${response.error.code}. Reason: ${response.error.description}`
        );
        console.error("Razorpay Error:", response.error);
        setIsLoading(false);
      });

      rzp1.open();
      setIsLoading(false);
      return true;
    },
    [currentUser, loadScript, onPaymentSuccess]
  );

  return { initializePayment, isLoading, error };
};

export default useRazorpay;

import { useState, useEffect, useCallback, useRef } from 'react';
import { 
  collection, 
  doc, 
  query, 
  where, 
  orderBy, 
  limit,
  onSnapshot,
  writeBatch,
  serverTimestamp,
  increment
} from 'firebase/firestore';
import { db } from '../firebase';
import { useAuth } from '../contexts/AuthContext';

// ============================================================================
// REAL-TIME HOOKS FOR FIREBASE OPERATIONS
// ============================================================================

/**
 * Generic hook for real-time Firestore listeners
 */
export const useRealtimeListener = (queryFn, dependencies = [], options = {}) => {
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const unsubscribeRef = useRef(null);
  const { enabled = true } = options;

  const sanitizeErrorMessage = (rawMsg) => {
    const r = String(rawMsg || '').toLowerCase();
    if (r.includes('requires an index')) {
      return 'Some data requires a Firestore composite index. Showing partial/fallback results where possible.';
    }
    return String(rawMsg || 'Unknown error');
  };

  useEffect(() => {
    if (!enabled) {
      setLoading(false);
      return;
    }

    setLoading(true);
    setError(null);

    try {
      const q = queryFn();
      if (!q) {
        setLoading(false);
        return;
      }

      unsubscribeRef.current = onSnapshot(
        q,
        (snapshot) => {
          const docs = snapshot.docs.map((d) => ({ id: d.id, ...d.data() }));
          setData(docs);
          setLoading(false);
        },
        async (err) => {
          console.error('Real-time listener error:', err);
          const raw = String(err?.message || err || '').toLowerCase();

          // If Firestore requires an index, and the caller provided a fallback
          // query factory, attempt to subscribe to the fallback and apply any
          // client-side filtering if supplied in options.
          if (raw.includes('requires an index') && typeof options.fallbackQueryFn === 'function') {
            console.warn('[useRealtimeListener] Index required; attempting fallback real-time query');
            try {
              // unsubscribe previous listener
              if (unsubscribeRef.current) {
                try { unsubscribeRef.current(); } catch (e) { /* ignore */ }
                unsubscribeRef.current = null;
              }

              const fallbackQ = options.fallbackQueryFn();
              if (!fallbackQ) {
                setError(sanitizeErrorMessage(err.message || err));
                setLoading(false);
                return;
              }

              unsubscribeRef.current = onSnapshot(
                fallbackQ,
                (snapshot) => {
                  let docs = snapshot.docs.map((d) => ({ id: d.id, ...d.data() }));
                  if (typeof options.clientSideFilter === 'function') {
                    docs = docs.filter(options.clientSideFilter);
                  }
                  setData(docs);
                  setError(null);
                  setLoading(false);
                },
                (fallbackErr) => {
                  console.error('[useRealtimeListener] Fallback real-time listener error:', fallbackErr);
                  setError(sanitizeErrorMessage(fallbackErr.message || fallbackErr));
                  setLoading(false);
                }
              );
              return;
            } catch (fallbackSetupErr) {
              console.error('[useRealtimeListener] Fallback setup failed:', fallbackSetupErr);
              setError(fallbackSetupErr.message || String(fallbackSetupErr));
              setLoading(false);
              return;
            }
          }

          setError(sanitizeErrorMessage(err.message || err));
          setLoading(false);
        }
      );
    } catch (err) {
      console.error('Query setup error:', err);
      setError(err.message || String(err));
      setLoading(false);
    }

    return () => {
      if (unsubscribeRef.current) {
        unsubscribeRef.current();
        unsubscribeRef.current = null;
      }
    };
  }, dependencies);

  return { data, loading, error };
};

/**
 * Real-time courses hook with filtering and sorting
 */
export const useRealtimeCourses = (options = {}) => {
  const { 
    limitCount = 50, 
    publishedOnly = true, 
    featuredOnly = false,
    category = null 
  } = options;

  const queryFn = useCallback(() => {
    const coursesRef = collection(db, 'courses');
    let q = query(coursesRef);

    // Apply filters
    if (publishedOnly) {
      q = query(q, where('isPublished', '==', true));
    }
    
    if (featuredOnly) {
      q = query(q, where('isFeatured', '==', true));
    }
    
    if (category) {
      q = query(q, where('category', '==', category));
    }

    // Apply sorting and limit
    q = query(q, orderBy('createdAt', 'desc'), limit(limitCount));

    return q;
  }, [limitCount, publishedOnly, featuredOnly, category]);

  // Provide a fallback real-time query (ordering only) and client-side filter
  const fallbackQueryFn = useCallback(() => {
    const coursesRef = collection(db, 'courses');
    return query(coursesRef, orderBy('createdAt', 'desc'), limit(limitCount));
  }, [limitCount]);

  const clientSideFilter = useCallback((doc) => {
    if (publishedOnly && !doc.isPublished) return false;
    if (featuredOnly && !doc.isFeatured) return false;
    if (category && doc.category !== category) return false;
    return true;
  }, [publishedOnly, featuredOnly, category]);

  return useRealtimeListener(queryFn, [limitCount, publishedOnly, featuredOnly, category], { fallbackQueryFn, clientSideFilter });
};

/**
 * Real-time single course hook
 */
export const useRealtimeCourse = (courseId, options = {}) => {
  const { enabled = true } = options;
  const [course, setCourse] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const unsubscribeRef = useRef(null);

  useEffect(() => {
    if (!enabled || !courseId) {
      setLoading(false);
      return;
    }

    setLoading(true);
    setError(null);

    const courseRef = doc(db, 'courses', courseId);
    
    unsubscribeRef.current = onSnapshot(
      courseRef,
      (doc) => {
        if (doc.exists()) {
          setCourse({ id: doc.id, ...doc.data() });
        } else {
          setCourse(null);
        }
        setLoading(false);
      },
      (err) => {
        console.error('Course listener error:', err);
        setError(err.message);
        setLoading(false);
      }
    );

    return () => {
      if (unsubscribeRef.current) {
        unsubscribeRef.current();
        unsubscribeRef.current = null;
      }
    };
  }, [courseId, enabled]);

  return { course, loading, error };
};

/**
 * Real-time user enrollments hook
 */
export const useRealtimeUserEnrollments = (userId, options = {}) => {
  const { enabled = true } = options;

  const queryFn = useCallback(() => {
    if (!userId) return null;
    
    const enrollmentsRef = collection(db, 'enrollments');
    return query(
      enrollmentsRef,
      where('userId', '==', userId),
      where('status', '==', 'SUCCESS'),
      orderBy('enrolledAt', 'desc')
    );
  }, [userId]);

  const fallbackEnrollmentsQuery = useCallback(() => {
    if (!userId) return null;
    const enrollmentsRef = collection(db, 'enrollments');
    return query(enrollmentsRef, where('userId', '==', userId), limit(200));
  }, [userId]);

  const enrollmentsClientFilter = useCallback((d) => d.status === 'SUCCESS', []);

  const { data: enrollments, loading, error } = useRealtimeListener(
    queryFn,
    [userId],
    { enabled: enabled && !!userId, fallbackQueryFn: fallbackEnrollmentsQuery, clientSideFilter: enrollmentsClientFilter }
  );

  const isEnrolled = useCallback((courseId) => {
    return enrollments.some(e => String(e.courseId) === String(courseId));
  }, [enrollments]);

  return { 
    enrollments, 
    loading, 
    error, 
    isEnrolled,
    enrollmentCount: enrollments.length 
  };
};

