import { useState, useEffect, useCallback, useRef } from 'react';
import { 
  collection, 
  query, 
  where, 
  orderBy, 
  limit,
  onSnapshot,
  addDoc,
  updateDoc,
  doc,
  serverTimestamp
} from 'firebase/firestore';
import { db } from '../firebase';
import { useAuth } from '../contexts/AuthContext';

// ============================================================================
// REAL-TIME NOTIFICATIONS HOOK
// ============================================================================

export const useRealtimeNotifications = (options = {}) => {
  const { currentUser, isAuthenticated } = useAuth();
  const { limitCount = 50, enabled = true } = options;
  
  const [notifications, setNotifications] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const unsubscribeRef = useRef(null);

  useEffect(() => {
    if (!enabled || !isAuthenticated || !currentUser?.uid) {
      setLoading(false);
      return;
    }

    setLoading(true);
    setError(null);

    const notificationsRef = collection(db, 'notifications');
    const q = query(
      notificationsRef,
      where('userId', '==', currentUser.uid),
      orderBy('createdAt', 'desc'),
      limit(limitCount)
    );

    unsubscribeRef.current = onSnapshot(
      q,
      (snapshot) => {
        const docs = snapshot.docs.map(doc => ({
          id: doc.id,
          ...doc.data()
        }));
        setNotifications(docs);
        setLoading(false);
      },
      (err) => {
        console.error('Notifications listener error:', err);
        setError(err.message);
        setLoading(false);
      }
    );

    return () => {
      if (unsubscribeRef.current) {
        unsubscribeRef.current();
        unsubscribeRef.current = null;
      }
    };
  }, [currentUser?.uid, isAuthenticated, enabled, limitCount]);

  const markAsRead = useCallback(async (notificationId) => {
    if (!currentUser?.uid) return { success: false, error: 'User not authenticated' };

    try {
      const notificationRef = doc(db, 'notifications', notificationId);
      await updateDoc(notificationRef, {
        isRead: true,
        readAt: serverTimestamp()
      });
      return { success: true };
    } catch (err) {
      console.error('Error marking notification as read:', err);
      return { success: false, error: err.message };
    }
  }, [currentUser?.uid]);

  const markAllAsRead = useCallback(async () => {
    if (!currentUser?.uid) return { success: false, error: 'User not authenticated' };

    try {
      const unreadNotifications = notifications.filter(n => !n.isRead);
      const updatePromises = unreadNotifications.map(notification => 
        updateDoc(doc(db, 'notifications', notification.id), {
          isRead: true,
          readAt: serverTimestamp()
        })
      );
      
      await Promise.all(updatePromises);
      return { success: true };
    } catch (err) {
      console.error('Error marking all notifications as read:', err);
      return { success: false, error: err.message };
    }
  }, [currentUser?.uid, notifications]);

  const deleteNotification = useCallback(async (notificationId) => {
    if (!currentUser?.uid) return { success: false, error: 'User not authenticated' };

    try {
      const notificationRef = doc(db, 'notifications', notificationId);
      await updateDoc(notificationRef, {
        isDeleted: true,
        deletedAt: serverTimestamp()
      });
      return { success: true };
    } catch (err) {
      console.error('Error deleting notification:', err);
      return { success: false, error: err.message };
    }
  }, [currentUser?.uid]);

  const unreadCount = notifications.filter(n => !n.isRead).length;

  return {
    notifications,
    loading,
    error,
    unreadCount,
    markAsRead,
    markAllAsRead,
    deleteNotification
  };
};

// ============================================================================
// NOTIFICATION CREATION HOOK (Admin use)
// ============================================================================

export const useNotificationCreator = () => {
  const { currentUser, isAuthenticated } = useAuth();
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const createNotification = useCallback(async (notificationData) => {
    if (!isAuthenticated || !currentUser?.uid) {
      setError('User not authenticated');
      return { success: false, error: 'User not authenticated' };
    }

    setLoading(true);
    setError(null);

    try {
      const notificationsRef = collection(db, 'notifications');
      const notificationPayload = {
        ...notificationData,
        createdAt: serverTimestamp(),
        isRead: false,
        isDeleted: false
      };

      const docRef = await addDoc(notificationsRef, notificationPayload);
      return { success: true, data: { id: docRef.id } };
    } catch (err) {
      console.error('Error creating notification:', err);
      setError(err.message);
      return { success: false, error: err.message };
    } finally {
      setLoading(false);
    }
  }, [currentUser?.uid, isAuthenticated]);

  const createBulkNotifications = useCallback(async (notificationsData) => {
    if (!isAuthenticated || !currentUser?.uid) {
      setError('User not authenticated');
      return { success: false, error: 'User not authenticated' };
    }

    setLoading(true);
    setError(null);

    try {
      const notificationsRef = collection(db, 'notifications');
      const timestamp = serverTimestamp();
      
      const notifications = notificationsData.map(data => ({
        ...data,
        createdAt: timestamp,
        isRead: false,
        isDeleted: false
      }));

      const promises = notifications.map(notification => 
        addDoc(notificationsRef, notification)
      );

      const results = await Promise.all(promises);
      return { success: true, data: results };
    } catch (err) {
      console.error('Error creating bulk notifications:', err);
      setError(err.message);
      return { success: false, error: err.message };
    } finally {
      setLoading(false);
    }
  }, [currentUser?.uid, isAuthenticated]);

  return {
    createNotification,
    createBulkNotifications,
    loading,
    error
  };
};

// ============================================================================
// NOTIFICATION TYPES AND HELPERS
