const CheckoutPage = () => {
  const { courseId } = useParams();
  const location = useLocation();
  const navigate = useNavigate();
  const { currentUser } = useAuth();
  const { getCourseById } = useCourseContext();
  const { validateCoupon, couponValidation, calculateDiscount } = usePayment();

  const [course, setCourse] = useState(null);
  const [billingInfo, setBillingInfo] = useState({
    name: currentUser?.displayName || "",
    email: currentUser?.email || "",
    phone: "",
    country: "India",
    agreeTerms: false,
  });
  const [loading, setLoading] = useState(true);
  const [paymentError, setPaymentError] = useState(null);
  const [couponCode, setCouponCode] = useState("");
  const [useTestPayment, setUseTestPayment] = useState(false);

  const handlePaymentSuccess = useCallback(
    (enrollmentId, courseId) => {
      alert("Enrollment successful! Redirecting to course content.");
      navigate(`/learn/${courseId}`);
    },
    [navigate]
  );

  const {
    initializePayment,
    isLoading: isPaymentGatewayLoading,
    error: paymentGatewayError,
  } = useRazorpay(currentUser, handlePaymentSuccess);

  // --- Course Data Loading Logic (Resilient) ---

  useEffect(() => {
    const load = async () => {
      let foundCourse = null;
      let loadingError = null;

      try {
        setLoading(true);
        setPaymentError(null);

        // 1. Try course from route state or context cache first (no extra calls)
        const passedCourse = location.state?.course || null;
        const ctxCourse = passedCourse || getCourseById(courseId);
        if (ctxCourse) {
          foundCourse = ctxCourse;
        }

        // 3. Final normalization for pricing fields
        if (foundCourse) {
          foundCourse = {
            id: String(foundCourse.id),
            title: foundCourse.title,
            price: Number(foundCourse.price || 5000),
            originalPrice: Number(
              foundCourse.originalPrice ||
                Number(foundCourse.price || 5000) + 1000
            ),
            platformDiscount:
              Number(
                (foundCourse.originalPrice || 0) - (foundCourse.price || 0)
              ) || 0,
            taxRate: 0.18,
          };
        } else {
          loadingError = "Course details unavailable. Please check the URL.";
        }
      } finally {
        if (foundCourse) {
          setCourse(foundCourse);
        }
        if (loadingError) {
          setPaymentError(loadingError);
        }

        // Initialize billing info
        if (currentUser) {
          setBillingInfo((prev) => ({
            ...prev,
            email: currentUser.email,
            name: currentUser.displayName || prev.name || "",
          }));
        }
        // Add a slight delay to let the shimmer be seen
        setTimeout(() => setLoading(false), 500);
      }
    };
    load();
  }, [courseId, currentUser, location.state?.course, getCourseById]);

  // Check if course data loaded before calculating prices and rendering form
  if (!course || loading) return <CheckoutShimmer />;

  // --- Calculation Logic ---
  const courseAmount = Number(course.price || 0);
  const platformDiscount = Number(course.platformDiscount || 0);
  const subtotal = courseAmount; // Assuming course.price already accounts for platform discount
  const taxRate = Number(course.taxRate || 0.18);
  const tax = subtotal * taxRate;

  const couponDiscount = couponValidation.isValid
    ? calculateDiscount(couponValidation, subtotal)
    : 0;

  const amountBeforeTaxAndCoupon = courseAmount + platformDiscount;
  const totalAmount = Math.max(0, subtotal - couponDiscount + tax);
  const totalSaved =
    Number(course.originalPrice || 0) - totalAmount + couponDiscount;

  // --- Handlers ---
  const handleInputChange = (e) => {
    const { name, value, type, checked } = e.target;
    setBillingInfo((prev) => ({
      ...prev,
      [name]: type === "checkbox" ? checked : value,
    }));
    setPaymentError(null);
  };

  const handleApplyCoupon = async (e) => {
    e.preventDefault();
    const trimmed = couponCode.trim().toUpperCase();
    if (!trimmed) return;
    await validateCoupon(trimmed, course.id); // Use course.id
  };

  const handleConfirmAndPay = async (e) => {
    e.preventDefault();
    setPaymentError(null);

    if (!billingInfo.agreeTerms) {
      setPaymentError(
        "You must agree to the Terms and Policies before proceeding."
      );
      return;
    }
    if (!billingInfo.phone || !billingInfo.name) {
      setPaymentError("Name and Phone number are required fields.");
      return;
    }
    if (!course) {
      setPaymentError("Course data is missing. Cannot initiate payment.");
      return;
    }

    // 🚨 CRITICAL: Check if totalAmount is zero or negative after all discounts
    if (totalAmount <= 0) {
      // Treat as a successful free/fully discounted enrollment
      try {
        const enrollmentDoc = await addDoc(collection(db, "enrollments"), {
          userId: currentUser.uid,
          courseId: course.id,
          courseTitle: course.title,
          status: "SUCCESS",
          paymentId: "FREE_ENROLLMENT_" + Math.random().toString(36).slice(2),
          amount: 0,
          coupon: couponValidation.isValid ? couponCode : null,
          couponDiscount: subtotal + tax,
          billingInfo,
          enrolledAt: new Date(),
          mode: "FREE",
        });
        handlePaymentSuccess(enrollmentDoc.id, course.id);
      } catch (err) {
        console.error("Free enrollment error", err);
        setPaymentError("Failed to record free enrollment in database.");
      }
      return;
    }

    // 1. Test payment path (For courses loaded from fallbackData)
    if (useTestPayment) {
      try {
        const enrollmentDoc = await addDoc(collection(db, "enrollments"), {
          userId: currentUser.uid,
          courseId: course.id,
          courseTitle: course.title,
          status: "SUCCESS",
          paymentId: "TEST_PAYMENT_" + Math.random().toString(36).slice(2),
          amount: totalAmount,
          coupon: couponValidation.isValid ? couponCode : null,
          couponDiscount,
          billingInfo,
          enrolledAt: new Date(),
          mode: "TEST",
        });
        handlePaymentSuccess(enrollmentDoc.id, course.id);
        return;
      } catch (err) {
        console.error("Test payment enrollment error", err);
        setPaymentError("Failed to record test enrollment in database.");
        return;
      }
    }

    // 2. 🚀 EXECUTE RAZORPAY PAYMENT 🚀
    // Convert amount to paise (integer)
    const amountInPaise = Math.round(totalAmount * 100);

    const success = await initializePayment({
      amount: amountInPaise, // <-- CRITICAL: Amount in PAISE
      currency: "INR",
      courseId: course.id,
      courseTitle: course.title,
      billingInfo: billingInfo,
      coupon: couponValidation.isValid ? couponCode : null,
      couponDiscount,
    });

    if (!success && paymentGatewayError) {
      setPaymentError(paymentGatewayError);
    }
  };

  // Determine the final error message to display
  const finalError = paymentError || paymentGatewayError;

  // --- Helper Component for Readability (PriceRow) ---
  const PriceRow = ({
    label,
    value,
    isTotal = false,
    isDiscount = false,
    isTax = false,
  }) => (
    <div
      className={`flex justify-between py-2 ${
        isTotal ? "border-t-2 border-dashed font-bold text-lg pt-3 mt-2" : ""
      }`}
    >
      <span
        className={`${
          isDiscount
            ? "text-red-500"
            : isTax
            ? "text-gray-700"
            : "text-gray-700"
        }`}
      >
        {label}
      </span>

      <span
        className={`${
          isDiscount ? "text-red-500" : isTotal ? "text-2xl" : "text-gray-900"
        } font-semibold`}
      >
        {isDiscount ? "- " : ""}₹{Math.abs(value).toFixed(2)}
      </span>
    </div>
  );

  const baseInputClasses =
    "w-full p-3 border border-gray-300 rounded-lg focus:ring-blue-600 focus:border-blue-600 transition";

  return (
    <PageContainer className="py-4 bg-gray-50 min-h-screen">
      <form
        onSubmit={handleConfirmAndPay}
        className="grid grid-cols-1 lg:grid-cols-3 gap-10"
      >
        {/* LEFT COLUMN: Billing Information Form */}
        <div className="lg:col-span-2 space-y-8 p-8 bg-white rounded-2xl shadow-xl border border-blue-100">
          <h2 className="text-2xl font-bold border-b-2 border-yellow-500 pb-3 text-gray-800 flex items-center gap-3">
            <GraduationCap
              className="w-6 h-6"
              style={{ color: PRIMARY_BLUE }}
            />
            Enrollment Checkout
          </h2>

          {/* Course Title Reminder */}
          <div className="bg-blue-50 border-l-4 border-blue-500 p-4 rounded-lg">
            <p className="font-semibold text-blue-800 text-lg">
              Enrolling in: {course.title}
            </p>
            {useTestPayment && (
              <p className="text-sm text-red-600 mt-1 font-medium">
                ⚠️ Test Mode: Using local course data. Payment will be
                simulated.
              </p>
            )}
          </div>

          {/* Form Inputs */}
          <div className="grid sm:grid-cols-2 gap-6">
            <div>
              <label className="block text-sm font-semibold text-gray-800 mb-1">
                Full Name *
              </label>
              <input
                type="text"
                name="name"
                value={billingInfo.name}
                onChange={handleInputChange}
                placeholder="Your Full Name"
                required
                className={`${baseInputClasses}`}
              />
            </div>
            <div>
              <label className="block text-sm font-semibold text-gray-800 mb-1">
                Email Address
              </label>
              <input
                type="email"
                name="email"
                value={billingInfo.email}
                readOnly
                disabled
                className={`${baseInputClasses} text-gray-900 bg-gray-100 cursor-not-allowed`}
              />
            </div>
            <div>
              <label className="block text-sm font-semibold text-gray-800 mb-1">
                Phone Number *
              </label>
              <input
                type="tel"
                name="phone"
                value={billingInfo.phone}
                onChange={handleInputChange}
                placeholder="+91-XXXXXXXXXX"
                required
                className={`${baseInputClasses}`}
              />
            </div>
            <div>
              <label className="block text-sm font-semibold text-gray-800 mb-1">
                Country
              </label>
              <input
                type="text"
                name="country"
                value={billingInfo.country}
                onChange={handleInputChange}
                placeholder="Country"
                className={`${baseInputClasses}`}
              />
            </div>
          </div>

          {/* Terms Checkbox (Added) */}
          <div className="pt-6 border-t border-gray-200 space-y-4">
            <div className="flex items-start space-x-3">
              <input
                type="checkbox"
                name="agreeTerms"
                checked={billingInfo.agreeTerms}
                onChange={handleInputChange}
                id="agreeTerms"
                className="mt-1 w-5 h-5 text-blue-600 border-gray-300 rounded focus:ring-blue-500"
              />
              <label
                htmlFor="agreeTerms"
                className="text-sm font-medium text-gray-700"
              >
                I agree to the{" "}
                <Link
                  to="/terms"
                  className="text-blue-600 hover:text-blue-800 font-semibold underline"
                >
                  Terms and Conditions
                </Link>{" "}
                and{" "}
                <Link
                  to="/privacy"
                  className="text-blue-600 hover:text-blue-800 font-semibold underline"
                >
                  Privacy Policy
                </Link>{" "}
                for this enrollment. *
              </label>
            </div>
          </div>

          {finalError && (
            <div className="bg-red-100 border-l-4 border-red-500 text-red-700 p-4 rounded-lg flex items-center gap-3">
              <X className="w-5 h-5" />
              <p className="font-medium">{finalError}</p>
            </div>
          )}
        </div>

        {/* RIGHT COLUMN: Order Summary (1/3 width) */}
        <div className="lg:col-span-1 space-y-6">
          <div className="bg-white p-6 rounded-2xl shadow-xl border border-blue-100 sticky top-28">
            <h2 className="text-xl font-bold mb-4 text-gray-800 border-b pb-2">
              Order Summary
            </h2>

            {/* Detailed Pricing */}
            <div className="space-y-1">
              <PriceRow label="Original Price" value={course.originalPrice} />
              <PriceRow
                label="Platform Discount"
                value={platformDiscount}
                isDiscount={true}
              />
              <PriceRow label="Amount (Pre-Tax)" value={subtotal} />

              {/* Coupon entry */}
              <div className="grid sm:grid-cols-3 gap-4 items-end mb-4 pt-2 border-t mt-2">
                <div className="sm:col-span-2">
                  <label className="block text-sm font-semibold text-gray-700 mb-1">
                    Have a coupon?
                  </label>
                  <input
                    type="text"
                    value={couponCode}
                    onChange={(e) =>
                      setCouponCode(e.target.value.toUpperCase())
                    }
                    placeholder="Enter coupon code"
                    className={`${baseInputClasses} outline-none`}
                  />
                </div>
                <button
                  type="button"
                  onClick={handleApplyCoupon}
                  className="h-12 bg-blue-600 text-white rounded-lg font-semibold hover:bg-blue-700 transition disabled:bg-gray-400"
                  disabled={couponValidation.loading}
                >
                  {couponValidation.loading ? "Checking..." : "Apply"}
                </button>
              </div>
              {couponValidation.error && (
                <p className="text-sm text-red-600">{couponValidation.error}</p>
              )}
              {couponValidation.isValid && (
                <p className="text-sm text-green-700">
                  Coupon applied: {couponValidation.coupon.name}
                </p>
              )}

              {couponDiscount > 0 && (
                <PriceRow
                  label={`Coupon Discount (${couponCode})`}
                  value={couponDiscount}
                  isDiscount={true}
                />
              )}

              <PriceRow
                label={`Tax (${(taxRate * 100).toFixed(0)}% GST)`}
                value={tax}
                isTax={true}
              />
            </div>

            {/* Total Final Price */}
            <PriceRow
              label="Total Amount Payable"
              value={totalAmount}
              isTotal={true}
            />

            {/* Total Savings */}
            <div className="bg-green-50 border border-green-300 p-4 mt-4 rounded-lg flex justify-between items-center text-green-700 font-bold shadow-inner">
              <span className="flex items-center gap-2">
                <CheckCircle className="w-5 h-5" /> Total Saved
              </span>
              <span className="text-xl">₹{totalSaved.toFixed(2)}</span>
            </div>

            {/* Confirm and Pay Button */}
            <button
              type="submit"
              disabled={
                isPaymentGatewayLoading ||
                !billingInfo.agreeTerms ||
                totalAmount < 0
              }
              className={`w-full h-14 rounded-xl text-lg font-bold text-white transition-all shadow-xl mt-6 ${
                billingInfo.agreeTerms
                  ? "bg-green-600 hover:bg-green-700 transform hover:scale-[1.01]"
                  : "bg-gray-400 cursor-not-allowed"
              }`}
            >
              {isPaymentGatewayLoading ? (
                "Initiating Secure Payment..."
              ) : (
                <>
                  <Lock className="w-5 h-5 inline mr-2" />
                  Confirm and Pay ₹{totalAmount.toFixed(2)}
                </>
              )}
            </button>
          </div>
        </div>
      </form>
    </PageContainer>
  );
};

export default CheckoutPage;

import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import { useRealtimeCourse, useRealtimeEnrollmentStatus, useRealtimeEnrollmentMutations } from '../hooks/useRealtimeFirebase';
import useRazorpay from '../hooks/useRazorpay';
import PageContainer from '../components/layout/PageContainer';
import { Card, CardContent, CardHeader, CardTitle } from '../components/ui/Card';
import Button from '../components/ui/Button';
import Badge from '../components/ui/Badge';
import { Alert, AlertDescription, AlertIcon } from '../components/ui/Alert';
import LoadingSpinner from '../components/ui/LoadingSpinner';
import Breadcrumbs from '../components/ui/breadcrumbs/Breadcrumbs';
import { Clock, Globe, Star, Award, CheckCircle, Users, BookOpen } from 'lucide-react';
import Pagecontainer from '../components/layout/PageContainer';

const CourseDetailsPage = () => {
  const { courseId } = useParams();
  const navigate = useNavigate();
  const { currentUser, isAuthenticated } = useAuth();
  
  const { course, loading: courseLoading, error: courseError } = useRealtimeCourse(courseId);
  const { isEnrolled, enrollment, loading: enrollmentLoading } = useRealtimeEnrollmentStatus(currentUser?.uid, courseId);
  const { createEnrollment, loading: enrollmentCreating } = useRealtimeEnrollmentMutations();
  
  const { initializePayment, isLoading: paymentLoading, error: paymentError } = useRazorpay(
    currentUser,
    (enrollmentId, courseId) => {
      navigate(`/learn/${courseId}`);
    }
  );

  const breadcrumbItems = [
    { label: "Home", link: "/" },
    { label: "Courses", link: "/courses" },
    { label: course?.title || "Course", link: `/course/${courseId}` },
  ];

  const handleEnroll = async () => {
    if (!isAuthenticated) {
      navigate('/auth/signin');
      return;
    }

    if (!course) return;

    const priceNumber = Number(course.price);
    if (!Number.isFinite(priceNumber) || priceNumber <= 0) {
      alert("Invalid course price. Please contact support.");
      return;
    }

    const paymentDetails = {
      courseId: course.id,
      courseTitle: course.title,
      amount: priceNumber,
      billingInfo: {
        name: currentUser.displayName || "Learner",
        email: currentUser.email,
      },
    };

    await initializePayment(paymentDetails);
  };

  if (courseLoading) {
    return (
      <PageContainer items={breadcrumbItems} className="min-h-screen">
         <div className="mt-8">
          <LoadingSpinner size="lg" message="Loading course details..." />
        </div>
      </PageContainer>
    );
  }

  if (courseError || !course) {
    return (
      <PageContainer items={breadcrumbItems} className="min-h-screen">
         <Alert variant="destructive" className="mt-8">
          <AlertIcon variant="destructive" />
          <AlertDescription>
            {courseError || "Course not found"}
          </AlertDescription>
        </Alert>
      </PageContainer>
    );
  }

  const price = Number(course.price) || 0;
  const originalPrice = Number(course.originalPrice) || (price + 2000);

  return (
    <PageContainer items={breadcrumbItems} className="min-h-screen py-8">
 
      <div className="grid grid-cols-1 lg:grid-cols-3 gap-8 mt-8">
        {/* Main Content */}
        <div className="lg:col-span-2 space-y-6">
          {/* Course Header */}
          <Card>
            <CardHeader>
              <div className="flex items-start justify-between">
                <div className="flex-1">
                  <CardTitle className="text-3xl font-bold mb-2">
                    {course.title}
                  </CardTitle>
                  <p className="text-muted-foreground text-lg">
                    {course.description || course.courseDescription}
                  </p>
                </div>
                {course.isBestseller && (
                  <Badge variant="success" className="ml-4">
                    <Award size={16} className="mr-1" />
                    Bestseller
                  </Badge>
                )}
              </div>
            </CardHeader>
            <CardContent>
              {/* Course Stats */}
              <div className="grid grid-cols-2 md:grid-cols-4 gap-4 mb-6">
                <div className="flex items-center gap-2">
                  <Clock size={20} className="text-muted-foreground" />
                  <span className="text-sm text-muted-foreground">
                    {course.duration || 'Self-Paced'}
                  </span>
                </div>
                <div className="flex items-center gap-2">
                  <Globe size={20} className="text-muted-foreground" />
                  <span className="text-sm text-muted-foreground">
                    {course.mode || 'Online'}
                  </span>
                </div>
                <div className="flex items-center gap-2">
                  <Star size={20} className="text-yellow-500 fill-yellow-500" />
                  <span className="text-sm text-muted-foreground">
                    {course.rating || '4.5'} Rating
                  </span>
                </div>
                <div className="flex items-center gap-2">
                  <Users size={20} className="text-muted-foreground" />
                  <span className="text-sm text-muted-foreground">
                    {course.students || 0} Students
                  </span>
                </div>
              </div>

              {/* Course Modules */}
              {course.modules && course.modules.length > 0 && (
                <div>
                  <h3 className="text-xl font-semibold mb-4 flex items-center gap-2">
                    <BookOpen size={20} />
                    Course Modules
                  </h3>
                  <div className="space-y-3">
                    {course.modules.map((module, index) => (
                      <div key={module.moduleKey || index} className="border border-border rounded-lg p-4">
                        <h4 className="font-medium mb-2">
                          {module.moduleTitle || module.title || `Module ${index + 1}`}
                        </h4>
                        {module.videos && module.videos.length > 0 && (
                          <div className="text-sm text-muted-foreground">
                            {module.videos.length} video{module.videos.length !== 1 ? 's' : ''}
                          </div>
                        )}
                      </div>
                    ))}
                  </div>
                </div>
              )}
            </CardContent>
          </Card>
        </div>

        {/* Sidebar */}
        <div className="space-y-6">
          {/* Course Image */}
          <Card>
            <div className="aspect-video overflow-hidden rounded-t-lg">
              <img
                src={course.imageUrl || "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSUwCJYSnbBLMEGWKfSnWRGC_34iCCKkxePpg&s"}
                alt={course.title}
                className="w-full h-full object-cover"
              />
            </div>
            <CardContent className="p-6">
              {/* Pricing */}
              <div className="text-center mb-6">
                <div className="text-3xl font-bold text-foreground">
                  ₹{price.toFixed(0)}
                </div>
                <div className="text-lg text-muted-foreground line-through">
                  ₹{originalPrice.toFixed(0)}
                </div>
                <div className="text-sm text-green-600 font-medium">
                  Save ₹{(originalPrice - price).toFixed(0)}
                </div>
              </div>

              {/* Enrollment Status */}
              {enrollmentLoading ? (
                <div className="text-center">
                  <LoadingSpinner size="sm" />
                  <p className="text-sm text-muted-foreground mt-2">
                    Checking enrollment status...
                  </p>
                </div>
              ) : isEnrolled ? (
                <div className="text-center">
                  <div className="flex items-center justify-center gap-2 mb-4">
                    <CheckCircle size={20} className="text-green-600" />
                    <span className="text-green-600 font-medium">Enrolled</span>
                  </div>
                  <Button asChild className="w-full">
                    <a href={`/learn/${course.id}`}>
                      Continue Learning
                    </a>
                  </Button>
                </div>
              ) : (
                <div className="space-y-4">
                  <Button
                    onClick={handleEnroll}
                    disabled={paymentLoading || enrollmentCreating}
                    className="w-full"
                    size="lg"
                  >
                    {paymentLoading || enrollmentCreating ? (
                      <>
                        <LoadingSpinner size="sm" className="mr-2" />
                        Processing...
                      </>
                    ) : (
                      'Enroll Now'
                    )}
                  </Button>
                  
                  {!isAuthenticated && (
                    <p className="text-sm text-muted-foreground text-center">
                      <Button variant="link" asChild>
                        <a href="/auth/signin">Sign in</a>
                      </Button> to enroll
                    </p>
                  )}
                </div>
              )}

              {/* Payment Error */}
              {paymentError && (
                <Alert variant="destructive" className="mt-4">
                  <AlertIcon variant="destructive" />
                  <AlertDescription>
                    {paymentError}
                  </AlertDescription>
                </Alert>
              )}
            </CardContent>
          </Card>
        </div>
      </div>
    </PageContainer>
  );
};

export default CourseDetailsPage;

import React, { useEffect, useState } from "react";
import { useAuth } from "../contexts/AuthContext";
import { useRealtime } from "../contexts/RealtimeContext";
import PageContainer from "../components/layout/PageContainer";
import CourseList from "../components/Course/CourseList";
import Breadcrumbs from "../components/ui/breadcrumbs/Breadcrumbs";
import { Alert, AlertDescription, AlertIcon } from "../components/ui/Alert";
import LoadingSpinner from "../components/ui/LoadingSpinner";

const CoursePage = () => {
  const { currentUser, isAuthenticated } = useAuth();
  const {
    courses,
    coursesLoading,
    coursesError,
    enrollments,
    enrollmentsLoading,
    isEnrolled,
  } = useRealtime();
  const [enrollmentStatus, setEnrollmentStatus] = useState({});

  const breadcrumbItems = [
    { label: "Home", link: "/" },
    { label: "Courses", link: "/courses" },
  ];

  // Calculate enrollment status
  useEffect(() => {
    if (!isAuthenticated || !currentUser) {
      setEnrollmentStatus({});
      return;
    }

    const status = courses.reduce((acc, course) => {
      acc[course.id] = isEnrolled(course.id);
      return acc;
    }, {});

    setEnrollmentStatus(status);
  }, [courses, enrollments, isAuthenticated, currentUser, isEnrolled]);

  // Show loading state
  if (coursesLoading || enrollmentsLoading) {
    return (
      <PageContainer className="min-h-screen" items={breadcrumbItems}>
        <div className="mt-8">
          <LoadingSpinner size="lg" message="Loading courses..." />
        </div>
      </PageContainer>
    );
  }

  return (
    <PageContainer className="min-h-screen" items={breadcrumbItems}>
      {/* Error Messages */}
      {coursesError && (
        <Alert variant="destructive" className="mt-4">
          <AlertIcon variant="destructive" />
          <AlertDescription>{coursesError}</AlertDescription>
        </Alert>
      )}

      {/* Page Title */}
      <div className="mt-8 mb-6">
        <h1 className="text-3xl font-bold text-foreground">
          Available Courses
        </h1>
        <p className="text-muted-foreground mt-2">
          Explore our comprehensive certification programs
        </p>
      </div>

      {/* Course List */}
      <CourseList
        courses={courses}
        loading={coursesLoading}
        error={coursesError}
        enrollmentStatus={enrollmentStatus}
        className="mt-6"
      />
    </PageContainer>
  );
};

