import React, { useState, useEffect } from "react";
import {
  useParams,
  useNavigate,
  Navigate,
  useLocation,
  useMatch,
} from "react-router-dom";
import { useAuth } from "../../contexts/AuthContext.jsx";
import {
  ArrowLeft,
  Save,
  Eye,
  DollarSign,
  Users,
  BookOpen,
  Image,
} from "lucide-react";
import { global_classnames } from "../../utils/classnames.js";
import BasicInfoTab from "../../components/Admin/BasicInfoTab.jsx.jsx";
import PricingTab from "../../components/Admin/PricingTab.jsx";
import ContentTab from "../../components/Admin/ContentTab.jsx";
import MediaTab from "../../components/Admin/MediaTab.jsx";
import PreviewTab from "../../components/Admin/PreviewTab.jsx";
import Breadcrumbs from "../../components/ui/breadcrumbs/Breadcrumbs.jsx";

const items = [
  { label: "Admin", link: "/admin" },
  { label: "Courses", link: "/admin/courses" },
  { label: "Create Course", link: null },
];

const CourseForm = () => {
  const { isAdmin } = useAuth();
  // The courseId will be the actual ID for edit, or 'new' for creation
  const { courseId } = useParams();
  const navigate = useNavigate();

  const location = useLocation();
  // Detect create route explicitly — prefer react-router's matcher for robustness
  const createMatch = useMatch("/admin/courses/create/new");
  // Fallback: check last path segment (robust against trailing slashes or router nesting)
  const segments = location.pathname.split("/").filter(Boolean);
  const lastSegment = segments[segments.length - 1] || "";
  const isCreatePath =
    createMatch != null ||
    /\/create\/new(\/|$|\?)/i.test(location.pathname) ||
    lastSegment.toLowerCase() === "new";
  // Also detect explicit edit route and prefer an explicit courseId when present
  const editMatch = useMatch("/admin/courses/edit/:courseId");
  const matchedEditId = editMatch?.params?.courseId;
  const effectiveCourseId = courseId ?? matchedEditId;
  const isEditCourse =
    typeof effectiveCourseId === "string" &&
    effectiveCourseId !== "new" &&
    effectiveCourseId !== "";
  const isNewCourse = (!isEditCourse && isCreatePath) || courseId === "new";

  const [loading, setLoading] = useState(true);
  const [saving, setSaving] = useState(false);
  const [course, setCourse] = useState(null);
  const [modules, setModules] = useState([]);
  const [activeTab, setActiveTab] = useState("basic");
  const [errors, setErrors] = useState({});

  if (!isAdmin) {
    return <Navigate to="/" replace />;
  }

  // Initial empty course state (moved inside the component as it's only needed here)
  const emptyCourse = {
    title: "",
    description: "",
    shortDescription: "",
    category: "web-development",
    instructor: "",
    price: 0,
    originalPrice: 0,
    duration: "",
    level: "beginner",
    language: "english",
    isPublished: false,
    isFeatured: false,
    isBestseller: false,
    imageUrl: "",
    videoUrl: "",
    tags: [],
    requirements: [],
    whatYouLearn: [],
  };

  /* --- Data Fetching and Initialization --- */
  useEffect(() => {
    const fetchCourseData = async () => {
      setLoading(true);
      try {
        if (!isEditCourse) {
          // Create flow: use empty defaults
          setCourse(emptyCourse);
          setModules([]);
        } else {
          // Edit flow: effectiveCourseId must be defined
          const idToLoad = effectiveCourseId;
          // Simulate API call for existing course data
          await new Promise((resolve) => setTimeout(resolve, 1000));
          setCourse({
            id: idToLoad,
            title: "React Masterclass (ID: " + idToLoad + ")", // Show ID for verification
            description:
              "Learn React from scratch with hands-on projects. Build real-world applications and master modern React development.",
            shortDescription:
              "Complete React development course with hands-on projects",
            category: "web-development",
            instructor: "John Doe",
            price: 2999,
            originalPrice: 4999,
            duration: "12 hours",
            level: "beginner",
            language: "english",
            isPublished: true,
            isFeatured: false,
            isBestseller: true,
            imageUrl:
              "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSUwCJYSnbBLMEGWKfSnWRGC_34iCCKkxePpg&s",
            videoUrl: "",
            tags: ["react", "javascript", "frontend"],
            requirements: [
              "Basic HTML/CSS knowledge",
              "JavaScript fundamentals",
            ],
            whatYouLearn: [
              "React fundamentals",
              "Hooks",
              "State management",
              "Project building",
            ],
          });

          setModules([
            {
              id: 1,
              title: "Introduction to React",
              description:
                "Get started with React basics and understand core concepts",
              order: 1,
              duration: "2 hours",
              lessons: [
                {
                  id: 1,
                  title: "What is React?",
                  duration: "15 min",
                  type: "video",
                  content: "https://example.com/video1",
                },
                {
                  id: 2,
                  title: "Setting up Environment",
                  duration: "30 min",
                  type: "video",
                  content: "https://example.com/video2",
                },
              ],
            },
            {
              id: 2,
              title: "React Components",
              description: "Learn about components and props in depth",
              order: 2,
              duration: "3 hours",
              lessons: [
                {
                  id: 3,
                  title: "Functional Components",
                  duration: "45 min",
                  type: "video",
                  content: "https://example.com/video3",
                },
              ],
            },
          ]);
        }
      } catch (error) {
        console.error("Failed to fetch course:", error);
        setCourse(null); // Set to null to show "Not Found" message
      } finally {
        setLoading(false);
      }
    };

    fetchCourseData();
  }, [courseId, location.pathname]);

  /* --- Handlers & Helpers (passed to child components) --- */

  const validateForm = () => {
    const newErrors = {};

    if (!course.title?.trim()) newErrors.title = "Course title is required";
    if (!course.shortDescription?.trim())
      newErrors.shortDescription = "Short description is required";
    if (!course.description?.trim())
      newErrors.description = "Description is required";
    if (!course.instructor?.trim())
      newErrors.instructor = "Instructor name is required";
    if (!course.duration?.trim()) newErrors.duration = "Duration is required";
    if (course.price < 0) newErrors.price = "Price must be positive";

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleCourseChange = (field, value) => {
    setCourse((prev) => ({
      ...prev,
      [field]: value,
    }));
    if (errors[field]) {
      setErrors((prev) => ({ ...prev, [field]: "" }));
    }
  };

  const handleModuleChange = (moduleIndex, field, value) => {
    setModules((prev) =>
      prev.map((module, index) =>
        index === moduleIndex ? { ...module, [field]: value } : module
      )
    );
  };

  const handleLessonChange = (moduleIndex, lessonIndex, field, value) => {
    setModules((prev) =>
      prev.map((module, index) => {
        if (index === moduleIndex) {
          const updatedLessons = module.lessons.map((lesson, lIndex) =>
            lIndex === lessonIndex ? { ...lesson, [field]: value } : lesson
          );
          return { ...module, lessons: updatedLessons };
        }
        return module;
      })
    );
  };

  const addModule = () => {
    const newModule = {
      id: Date.now(),
      title: "New Module",
      description: "",
      order: modules.length + 1,
      duration: "1 hour",
      lessons: [],
    };
    setModules((prev) => [...prev, newModule]);
  };

  const deleteModule = (moduleIndex) => {
    if (window.confirm("Are you sure you want to delete this module?")) {
      setModules((prev) => prev.filter((_, index) => index !== moduleIndex));
    }
  };

  const addLesson = (moduleIndex) => {
    const newLesson = {
      id: Date.now(),
      title: "New Lesson",
      duration: "15 min",
      type: "video",
      content: "",
    };

    setModules((prev) =>
      prev.map((module, index) => {
        if (index === moduleIndex) {
          return {
            ...module,
            lessons: [...module.lessons, newLesson],
          };
        }
        return module;
      })
    );
  };

  const deleteLesson = (moduleIndex, lessonIndex) => {
    if (window.confirm("Are you sure you want to delete this lesson?")) {
      setModules((prev) =>
        prev.map((module, index) => {
          if (index === moduleIndex) {
            return {
              ...module,
              lessons: module.lessons.filter(
                (_, lIndex) => lIndex !== lessonIndex
              ),
            };
          }
          return module;
        })
      );
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!validateForm()) {
      setActiveTab(Object.keys(errors)[0] === "price" ? "pricing" : "basic"); // Switch to first tab with error
      alert("Please fix the errors before submitting.");
      return;
    }

    setSaving(true);

    try {
      // Placeholder for real API call (POST for new, PUT for edit)
      await new Promise((resolve) => setTimeout(resolve, 2000));
      console.log("Course saved:", { course, modules });
      alert(
        isNewCourse
          ? "Course created successfully!"
          : "Course updated successfully!"
      );
      navigate("/admin/courses");
    } catch (error) {
      console.error("Failed to save course:", error);
      alert("Failed to save course. Please try again.");
    } finally {
      setSaving(false);
    }
  };

  // Functions for Preview/Content Tabs
  const calculateTotalDuration = () => {
    return modules.reduce((total, module) => {
      const match = module.duration.match(/(\d+)\s*hour/i);
      const moduleHours = match ? parseInt(match[1]) : 0;
      return total + moduleHours;
    }, 0);
  };

  const calculateTotalLessons = () => {
    return modules.reduce((total, module) => total + module.lessons.length, 0);
  };

  /* --- Render Logic --- */

  if (loading) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600"></div>
      </div>
    );
  }

  // This check is important if the API returns a 404 for an invalid ID
  if (!course) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-center">
          <h2 className="text-2xl font-bold text-gray-900 mb-2">
            Course Not Found
          </h2>
          <p className="text-gray-600">
            The course you're looking for doesn't exist.
          </p>
        </div>
      </div>
    );
  }

  const tabComponents = {
    basic: (
      <BasicInfoTab
        course={course}
        handleCourseChange={handleCourseChange}
        errors={errors}
      />
    ),
    pricing: (
      <PricingTab
        course={course}
        handleCourseChange={handleCourseChange}
        errors={errors}
      />
    ),
    content: (
      <ContentTab
        modules={modules}
        handleModuleChange={handleModuleChange}
        handleLessonChange={handleLessonChange}
        addModule={addModule}
        deleteModule={deleteModule}
        addLesson={addLesson}
        deleteLesson={deleteLesson}
        calculateTotalLessons={calculateTotalLessons}
        calculateTotalDuration={calculateTotalDuration}
      />
    ),
    media: <MediaTab course={course} handleCourseChange={handleCourseChange} />,
    preview: (
      <PreviewTab
        course={course}
        modules={modules}
        calculateTotalLessons={calculateTotalLessons}
        calculateTotalDuration={calculateTotalDuration}
      />
    ),
  };

  return (
    <div className="min-h-screen bg-gray-50 py-8">
      <div
      className={`${global_classnames.width.container} min-h-screen mx-auto px-4 sm:px-6 lg:px-8 py-4`}
      >
        {/* Header and Tabs */}
        
          <Breadcrumbs items={items} />
          <div className="flex flex-col  gap-4 mb-4">
            <div>
              <h1 className="text-3xl font-extrabold italic text-gray-900">
                {isNewCourse
                  ? "Create New Course"
                  : `Edit Course: ${course.title}`}
              </h1>
              <p className="text-gray-600 mt-1">
                {isNewCourse
                  ? "Add a new course to the platform"
                  : `Editing Course ID: ${course.id}`}
              </p>
            </div>
          

          {/* Tabs Navigation */}
          <div className="flex justify-between space-x-1 bg-white p-1 rounded-lg shadow-sm border border-gray-200 mb-6 overflow-x-auto">
            <div className="flex justify-between space-x-1">
              {[
                { id: "basic", label: "Basic Info", icon: BookOpen },
                { id: "pricing", label: "Pricing", icon: DollarSign },
                { id: "content", label: "Course Content", icon: Users },
                { id: "media", label: "Media", icon: Image },
              ].map((tab) => (
                <button
                  key={tab.id}
                  onClick={() => setActiveTab(tab.id)}
                  className={`flex items-center gap-2 px-4 py-2 rounded-md text-sm font-medium transition-colors whitespace-nowrap ${
                    activeTab === tab.id
                      ? "bg-blue-600 text-white"
                      : "text-gray-600 hover:text-gray-900 hover:bg-gray-100"
                  }`}
                >
                  <tab.icon className="w-4 h-4" />
                  {tab.label}
                </button>
              ))}
            </div>

            <div className="flex justify-end gap-4 pt-6 border-t border-gray-200">
              <button
                onClick={() => setActiveTab("preview")}
                className="flex items-center gap-2 px-6 py-3 bg-gray-800 text-white rounded-lg hover:bg-gray-900 transition-colors font-medium"
              >
                <Eye /> Preview
              </button>
              <button
                type="button"
                onClick={() => navigate("/admin/courses")}
                className="px-6 py-3 text-gray-700 bg-gray-200 rounded-lg hover:bg-gray-300 transition-colors font-medium"
              >
                Cancel
              </button>
              <button
                type="submit"
                disabled={saving}
                className="flex items-center gap-2 px-6 py-3 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition-colors font-medium disabled:opacity-50 disabled:cursor-not-allowed"
              >
                <Save className="w-4 h-4" />
                {saving
                  ? "Saving..."
                  : isNewCourse
                  ? "Create Course"
                  : "Save Changes"}
              </button>
            </div>
          </div>
        </div>

        <form onSubmit={handleSubmit} className="space-y-6">
          {/* Dynamic Tab Content */}
          {tabComponents[activeTab]}
        </form>
      </div>
    </div>
  );
};

export default CourseForm;

// src/pages/admin/AdminCourses.jsx

import React, { useState, useEffect } from "react";
import { useAuth } from "../../contexts/AuthContext.jsx";
import { Navigate, Link } from "react-router-dom";
import { Plus, Search, BookOpen, Users, Star, Clock } from "lucide-react";
import { global_classnames } from "../../utils/classnames.js";
import CourseCard from "../../components/Course/CourseCard.jsx";
import PageContainer from "../../components/layout/PageContainer.jsx";

const items = [
  { label: "Admin", link: "/admin" },
  { label: "Courses", link: "/admin/courses" },
];

const Courses = () => {
  const { isAdmin } = useAuth();
  const [courses, setCourses] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState("");

  if (!isAdmin) {
    return <Navigate to="/" replace />;
  }

  useEffect(() => {
    const timer = setTimeout(() => {
      setCourses([
        {
          id: 1,
          title: "React Masterclass",
          instructor: "John Doe",
          category: "Web Development",
          price: 2999,
          students: 1247,
          rating: 4.8,
          duration: "12 hours",
          status: "published",
          createdAt: "2024-01-15",
        },
        {
          id: 2,
          title: "Node.js Fundamentals",
          instructor: "Jane Smith",
          category: "Backend Development",
          price: 1999,
          students: 856,
          rating: 4.6,
          duration: "8 hours",
          status: "published",
          createdAt: "2024-01-20",
        },
        {
          id: 3,
          title: "Advanced Python Programming",
          instructor: "Bob Johnson",
          category: "Programming",
          price: 2499,
          students: 0,
          rating: 0,
          duration: "10 hours",
          status: "draft",
          createdAt: "2024-02-01",
        },
      ]);
      setLoading(false);
    }, 1000);
    return () => clearTimeout(timer);
  }, []);

  const filteredCourses = courses.filter(
    (course) =>
      course.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
      course.instructor.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const handleEditCourse = (course) => {
    // This will be handled by the Link in CourseCard
    console.log("Edit course:", course);
  };

  const handleDeleteCourse = (course) => {
    if (window.confirm(`Are you sure you want to delete "${course.title}"?`)) {
      setCourses((prev) => prev.filter((c) => c.id !== course.id));
    }
  };

  const handleViewCourse = (course) => {
    // Navigate to course preview or public page
    window.open(`/course/${course.id}`, "_blank");
  };

  return (
    <PageContainer items={items} className="min-h-screen bg-gray-50 py-8">
        <div className="flex flex-col  gap-4 mb-4">
          <div className="flex flex-col">
            <h1 className="text-3xl font-extrabold text-gray-900 italic">
              Course Management
            </h1>
            <p className="text-gray-600 mt-1">
              Create and manage platform courses
            </p>

             <div className="flex  sm:flex-row sm:items-center sm:justify-between gap-4">
            <div className="flex-1 max-w-md">
              <div className="relative">
                <Search className="w-5 h-5 text-gray-400 absolute left-3 top-1/2 transform -translate-y-1/2" />
                <input
                  type="text"
                  placeholder="Search courses..."
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                  className="w-full pl-10 pr-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                />
              </div>
            </div>
            <Link
              to="/admin/courses/create/new"
              className="flex items-center space-x-2 px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition-colors"
            >
              <Plus className="w-4 h-4" />
              <span>Create Course</span>
            </Link>
          </div>
          </div>

         
        </div>

        {/* Quick Stats */}
        <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
          <div className="bg-white p-4 rounded-lg shadow border border-gray-200 text-center">
            <BookOpen className="w-8 h-8 text-blue-600 mx-auto mb-2" />
            <div className="text-2xl font-bold text-gray-900">
              {courses.length}
            </div>
            <div className="text-sm text-gray-600">Total Courses</div>
          </div>
          <div className="bg-white p-4 rounded-lg shadow border border-gray-200 text-center">
            <Users className="w-8 h-8 text-green-600 mx-auto mb-2" />
            <div className="text-2xl font-bold text-gray-900">
              {courses.filter((c) => c.status === "published").length}
            </div>
            <div className="text-sm text-gray-600">Published</div>
          </div>
          <div className="bg-white p-4 rounded-lg shadow border border-gray-200 text-center">
            <Star className="w-8 h-8 text-yellow-600 mx-auto mb-2" />
            <div className="text-2xl font-bold text-gray-900">
              {courses.reduce((acc, course) => acc + course.students, 0)}
            </div>
            <div className="text-sm text-gray-600">Total Students</div>
          </div>
          <div className="bg-white p-4 rounded-lg shadow border border-gray-200 text-center">
            <Clock className="w-8 h-8 text-purple-600 mx-auto mb-2" />
            <div className="text-2xl font-bold text-gray-900">
              {courses.filter((c) => c.status === "draft").length}
            </div>
            <div className="text-sm text-gray-600">Drafts</div>
          </div>
        </div>

        {/* Courses Grid */}
        <div className="flex flex-wrap items-center my-4">
          {filteredCourses.map((course) => (
            <CourseCard
              key={course.id}
              course={course}
              showAdminOptions={true}
              onEdit={handleEditCourse}
              onDelete={handleDeleteCourse}
              onView={handleViewCourse}
            />
          ))}
        </div>
      </PageContainer>
  );
};

export default Courses;

// src/pages/admin/AdminUsers.jsx

import React, { useState, useEffect } from 'react';
import { useAuth } from '../../contexts/AuthContext.jsx';
import { Navigate } from 'react-router-dom';
import { Search, Filter, Mail, Phone, Calendar, Shield, UserPlus, X, BookOpen, Send } from 'lucide-react';
import { global_classnames } from '../../utils/classnames.js';
import { getAllUsersData, createUserWithCredentials } from '../../firebase/services_modular/userOperations';
import { getAllCourses } from '../../firebase/services_modular/courseOperations';
import { createEnrollment } from '../../firebase/services_modular/enrollmentOperations';
import PageContainer from '../../components/layout/PageContainer.jsx';
const items = [
    { label: 'Admin', link: '/admin' },
    { label: 'User Management', link: '/admin/users' },

]
const UsersManagement = () => {
    const { isAdmin } = useAuth();
    const [users, setUsers] = useState([]);
    const [loading, setLoading] = useState(true);
    const [searchTerm, setSearchTerm] = useState('');
    const [showCreateModal, setShowCreateModal] = useState(false);
    const [showEnrollModal, setShowEnrollModal] = useState(false);
    const [selectedUser, setSelectedUser] = useState(null);
    const [courses, setCourses] = useState([]);
    const [formData, setFormData] = useState({
        name: '',
        email: '',
        phone: '',
        password: ''
    });
    const [selectedCourses, setSelectedCourses] = useState([]);
    const [submitting, setSubmitting] = useState(false);
    const [error, setError] = useState('');
    const [success, setSuccess] = useState('');

    if (!isAdmin) {
        return <Navigate to="/" replace />;
    }

    useEffect(() => {
        fetchUsers();
        fetchCourses();
    }, []);

    const fetchUsers = async () => {
        setLoading(true);
        const result = await getAllUsersData(500);
        if (result.success) {
            setUsers(result.data);
        }
        setLoading(false);
    };

    const fetchCourses = async () => {
        const result = await getAllCourses();
        if (result.success) {
            setCourses(result.data);
        }
    };

    const handleCreateUser = async (e) => {
        e.preventDefault();
        setError('');
        setSuccess('');
        setSubmitting(true);

        try {
            const result = await createUserWithCredentials({
                email: formData.email,
                password: formData.password,
                displayName: formData.name,
                phone: formData.phone
            });

            if (result.success) {
                setSuccess(`User created successfully! Credentials: ${formData.email} / ${formData.password}`);
                setFormData({ name: '', email: '', phone: '', password: '' });
                fetchUsers();
                setTimeout(() => {
                    setShowCreateModal(false);
                    setSuccess('');
                }, 3000);
            } else {
                setError(result.error || 'Failed to create user');
            }
        } catch (err) {
            setError('An error occurred while creating the user');
        } finally {
            setSubmitting(false);
        }
    };

    const handleEnrollUser = async () => {
        if (!selectedUser || selectedCourses.length === 0) {
            setError('Please select at least one course');
            return;
        }

        setSubmitting(true);
        setError('');
        setSuccess('');

        try {
            for (const courseId of selectedCourses) {
                const course = courses.find(c => c.courseId === courseId);
                await createEnrollment({
                    userId: selectedUser.uid,
                    courseId: courseId,
                    courseTitle: course?.title || 'Course',
                    status: 'SUCCESS',
                    paymentData: {
                        amount: 0,
                        paymentId: 'ADMIN_ENROLLMENT'
                    }
                });
            }
            setSuccess(`Successfully enrolled ${selectedUser.displayName || selectedUser.email} in ${selectedCourses.length} course(s)`);
            setSelectedCourses([]);
            fetchUsers();
            setTimeout(() => {
                setShowEnrollModal(false);
                setSuccess('');
            }, 2000);
        } catch (err) {
            setError('Failed to enroll user in courses');
        } finally {
            setSubmitting(false);
        }
    };

    const filteredUsers = users.filter(user => {
        const name = (user.displayName || '').toLowerCase();
        const email = (user.email || '').toLowerCase();
        const search = searchTerm.toLowerCase();
        return name.includes(search) || email.includes(search);
    });

    const getStatusColor = (status) => {
        switch (status) {
            case 'active': return 'bg-green-100 text-green-800';
            case 'inactive': return 'bg-red-100 text-red-800';
            default: return 'bg-gray-100 text-gray-800';
        }
    };

    const getRoleColor = (role) => {
        switch (role) {
            case 'admin': return 'bg-purple-100 text-purple-800';
            case 'instructor': return 'bg-blue-100 text-blue-800';
            case 'student': return 'bg-green-100 text-green-800';
            default: return 'bg-gray-100 text-gray-800';
        }
    };

    return (
        <PageContainer items={items} className="min-h-screen bg-gray-50 py-8">
                <div className="mb-8">
                    <div className="flex items-center gap-4 mb-4">
                        
                        <div>
                            <h1 className="text-3xl font-extrabold italic text-gray-900">User Management</h1>
                            <p className="text-gray-600 mt-1">Manage platform users and their permissions</p>
                        </div>
                    </div>

                    <div className="flex flex-col sm:flex-row sm:items-center sm:justify-between gap-4">
                        <div className="flex-1 max-w-md">
                            <div className="relative">
                                <Search className="w-5 h-5 text-gray-400 absolute left-3 top-1/2 transform -translate-y-1/2" />
                                <input
                                    type="text"
                                    placeholder="Search users by name or email..."
                                    value={searchTerm}
                                    onChange={(e) => setSearchTerm(e.target.value)}
                                    className="w-full pl-10 pr-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                                />
                            </div>
                        </div>
                        <button
                            onClick={() => setShowCreateModal(true)}
                            className="flex items-center space-x-2 px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition-colors"
                        >
                            <UserPlus className="w-4 h-4" />
                            <span>Add User</span>
                        </button>
                    </div>
                </div>

                {/* Users Table */}
                <div className="bg-white rounded-xl shadow-md border border-gray-200 overflow-hidden">
                    {loading ? (
                        <div className="p-8 text-center">
                            <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600 mx-auto"></div>
                            <p className="text-gray-600 mt-4">Loading users...</p>
                        </div>
                    ) : (
                        <div className="overflow-x-auto">
                            <table className="min-w-full divide-y divide-gray-200">
                                <thead className="bg-gray-50">
                                    <tr>
                                        <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                                            User
                                        </th>
                                        <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                                            Role
                                        </th>
                                        <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                                            Status
                                        </th>
                                        <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                                            Courses
                                        </th>
                                        <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                                            Join Date
                                        </th>
                                        <th className="px-6 py-3 text-right text-xs font-medium text-gray-500 uppercase tracking-wider">
                                            Actions
                                        </th>
                                    </tr>
                                </thead>
                                <tbody className="bg-white divide-y divide-gray-200">
                                    {filteredUsers.map((user) => (
                                        <tr key={user.uid} className="hover:bg-gray-50 transition-colors">
                                            <td className="px-6 py-4 whitespace-nowrap">
                                                <div className="flex items-center">
                                                    <div className="flex-shrink-0 h-10 w-10 bg-blue-100 rounded-full flex items-center justify-center">
                                                        <span className="text-blue-600 font-semibold">
                                                            {(user.displayName || user.email || 'U').substring(0, 2).toUpperCase()}
                                                        </span>
                                                    </div>
                                                    <div className="ml-4">
                                                        <div className="text-sm font-medium text-gray-900">
                                                            {user.displayName || 'No Name'}
                                                        </div>
                                                        <div className="text-sm text-gray-500 flex items-center">
                                                            <Mail className="w-3 h-3 mr-1" />
                                                            {user.email}
                                                        </div>
                                                    </div>
                                                </div>
                                            </td>
                                            <td className="px-6 py-4 whitespace-nowrap">
                                                <span className={`inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium ${getRoleColor(user.isAdmin ? 'admin' : 'student')}`}>
                                                    <Shield className="w-3 h-3 mr-1" />
                                                    {user.isAdmin ? 'admin' : 'student'}
                                                </span>
                                            </td>
                                            <td className="px-6 py-4 whitespace-nowrap">
                                                <span className={`inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium ${getStatusColor('active')}`}>
                                                    active
                                                </span>
                                            </td>
                                            <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                                                {user.totalCoursesEnrolled || 0} courses
                                            </td>
                                            <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                                                <div className="flex items-center">
                                                    <Calendar className="w-3 h-3 mr-1" />
                                                    {user.createdAt?.toDate?.()?.toLocaleDateString() || 'N/A'}
                                                </div>
                                            </td>
                                            <td className="px-6 py-4 whitespace-nowrap text-right text-sm font-medium">
                                                <button
                                                    onClick={() => {
                                                        setSelectedUser(user);
                                                        setShowEnrollModal(true);
                                                    }}
                                                    className="text-blue-600 hover:text-blue-900 mr-3 flex items-center gap-1"
                                                >
                                                    <BookOpen className="w-4 h-4" />
                                                    Enroll
                                                </button>
                                            </td>
                                        </tr>
                                    ))}
                                </tbody>
                            </table>
                        </div>
                    )}
                </div>

                {/* Summary Stats */}
                <div className="grid grid-cols-1 md:grid-cols-4 gap-4 mt-6">
                    <div className="bg-white p-4 rounded-lg shadow border border-gray-200 text-center">
                        <div className="text-2xl font-bold text-blue-600">{users.length}</div>
                        <div className="text-sm text-gray-600">Total Users</div>
                    </div>
                    <div className="bg-white p-4 rounded-lg shadow border border-gray-200 text-center">
                        <div className="text-2xl font-bold text-green-600">
                            {users.filter(u => u.status === 'active').length}
                        </div>
                        <div className="text-sm text-gray-600">Active Users</div>
                    </div>
                    <div className="bg-white p-4 rounded-lg shadow border border-gray-200 text-center">
                        <div className="text-2xl font-bold text-purple-600">
                            {users.filter(u => u.role === 'instructor').length}
                        </div>
                        <div className="text-sm text-gray-600">Instructors</div>
                    </div>
                    <div className="bg-white p-4 rounded-lg shadow border border-gray-200 text-center">
                        <div className="text-2xl font-bold text-orange-600">
                            {users.reduce((acc, user) => acc + (user.totalCoursesEnrolled || 0), 0)}
                        </div>
                        <div className="text-sm text-gray-600">Total Enrollments</div>
                    </div>
                </div>

                {showCreateModal && (
                    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
                        <div className="bg-white rounded-xl max-w-md w-full p-6">
                            <div className="flex justify-between items-center mb-4">
                                <h2 className="text-2xl font-bold text-gray-900">Create User</h2>
                                <button onClick={() => setShowCreateModal(false)} className="text-gray-500 hover:text-gray-700">
                                    <X className="w-6 h-6" />
                                </button>
                            </div>

                            {error && (
                                <div className="mb-4 p-3 bg-red-50 border border-red-200 text-red-800 rounded-lg">
                                    {error}
                                </div>
                            )}

                            {success && (
                                <div className="mb-4 p-3 bg-green-50 border border-green-200 text-green-800 rounded-lg">
                                    {success}
                                </div>
                            )}

                            <form onSubmit={handleCreateUser} className="space-y-4">
                                <div>
                                    <label className="block text-sm font-medium text-gray-700 mb-1">Name</label>
                                    <input
                                        type="text"
                                        required
                                        value={formData.name}
                                        onChange={(e) => setFormData({ ...formData, name: e.target.value })}
                                        className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                                    />
                                </div>

                                <div>
                                    <label className="block text-sm font-medium text-gray-700 mb-1">Email</label>
                                    <input
                                        type="email"
                                        required
                                        value={formData.email}
                                        onChange={(e) => setFormData({ ...formData, email: e.target.value })}
                                        className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                                    />
                                </div>

                                <div>
                                    <label className="block text-sm font-medium text-gray-700 mb-1">Phone</label>
                                    <input
                                        type="tel"
                                        value={formData.phone}
                                        onChange={(e) => setFormData({ ...formData, phone: e.target.value })}
                                        className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                                    />
                                </div>

                                <div>
                                    <label className="block text-sm font-medium text-gray-700 mb-1">Password</label>
                                    <input
                                        type="text"
                                        required
                                        value={formData.password}
                                        onChange={(e) => setFormData({ ...formData, password: e.target.value })}
                                        className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                                        placeholder="Minimum 6 characters"
                                    />
                                </div>

                                <div className="flex gap-3 mt-6">
                                    <button
                                        type="button"
                                        onClick={() => setShowCreateModal(false)}
                                        className="flex-1 px-4 py-2 border border-gray-300 rounded-lg hover:bg-gray-50"
                                        disabled={submitting}
                                    >
                                        Cancel
                                    </button>
                                    <button
                                        type="submit"
                                        className="flex-1 px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 disabled:opacity-50"
                                        disabled={submitting}
                                    >
                                        {submitting ? 'Creating...' : 'Create User'}
                                    </button>
                                </div>
                            </form>
                        </div>
                    </div>
                )}

                {showEnrollModal && selectedUser && (
                    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
                        <div className="bg-white rounded-xl max-w-md w-full p-6">
                            <div className="flex justify-between items-center mb-4">
                                <h2 className="text-2xl font-bold text-gray-900">Enroll User</h2>
                                <button onClick={() => setShowEnrollModal(false)} className="text-gray-500 hover:text-gray-700">
                                    <X className="w-6 h-6" />
                                </button>
                            </div>

                            <p className="text-gray-600 mb-4">Select courses to enroll <strong>{selectedUser.displayName || selectedUser.email}</strong></p>

                            {error && (
                                <div className="mb-4 p-3 bg-red-50 border border-red-200 text-red-800 rounded-lg">
                                    {error}
                                </div>
                            )}

                            {success && (
                                <div className="mb-4 p-3 bg-green-50 border border-green-200 text-green-800 rounded-lg">
                                    {success}
                                </div>
                            )}

                            <div className="max-h-96 overflow-y-auto space-y-2 mb-4">
                                {courses.map((course) => (
                                    <label key={course.courseId} className="flex items-start p-3 border border-gray-200 rounded-lg hover:bg-gray-50 cursor-pointer">
                                        <input
                                            type="checkbox"
                                            checked={selectedCourses.includes(course.courseId)}
                                            onChange={(e) => {
                                                if (e.target.checked) {
                                                    setSelectedCourses([...selectedCourses, course.courseId]);
                                                } else {
                                                    setSelectedCourses(selectedCourses.filter(id => id !== course.courseId));
                                                }
                                            }}
                                            className="mt-1 mr-3"
                                        />
                                        <div className="flex-1">
                                            <div className="font-medium text-gray-900">{course.title}</div>
                                            <div className="text-sm text-gray-500">{course.category}</div>
                                        </div>
                                    </label>
                                ))}
                            </div>

                            <div className="flex gap-3">
                                <button
                                    onClick={() => setShowEnrollModal(false)}
                                    className="flex-1 px-4 py-2 border border-gray-300 rounded-lg hover:bg-gray-50"
                                    disabled={submitting}
                                >
                                    Cancel
                                </button>
                                <button
                                    onClick={handleEnrollUser}
                                    className="flex-1 px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 disabled:opacity-50"
                                    disabled={submitting}
                                >
                                    {submitting ? 'Enrolling...' : 'Enroll'}
                                </button>
                            </div>
                        </div>
                    </div>
                )}
            
        </PageContainer>
    );
};

